INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26205, 'Rivastig 10 mg/5ml syrup 120 ml', 'ريفاستينج10ملجرام/5مل شراب120مل', '90', NULL, 'Anticholinesterase', 'Rivastigmine', 'Global napi pharmaceuticals', 'Syrup', 'شراب', '10 mg', '1', NULL, 'about rivastigmine a central reversible non competitive anticholinesterase antidementia. mechanism of action of rivastigmine alzheimer`s disease may result from a deficiency in neurotransmitters used by nerves in the brain to communicate with one another.', NULL, NULL, 747, '2022-07-11', 'For the treatment of mild to moderate dementia associated with Parkinson''s disease or of the Alzheimer''s type.', 'Rivastigmine is a carbamate derivative that is structurally related to physostigmine, but not to donepezil and tacrine. The precise mechanism of rivastigmine has not been fully determined, but it is suggested that rivastigmine binds reversibly with and inactivates chlolinesterase (eg. acetylcholinesterase, butyrylcholinesterase), preventing the hydrolysis of acetycholine, and thus leading to an increased concentration of acetylcholine at cholinergic synapses. The anticholinesterase activity of rivastigmine is relatively specific for brain acetylcholinesterase and butyrylcholinesterase compared with those in peripheral tissues.', 'Rivastigmine is a parasympathomimetic and a reversible cholinesterase inhibitor. An early pathophysiological feature of Alzheimer''s disease that is associated with memory loss and cognitive deficits is a deficiency of acetylcholine as a result of selective loss of cholinergic neurons in the cerebral cortex, nucleus basalis, and hippocampus. Tacrine is postulated to exert its therapeutic effect by enhancing cholinergic function. While the precise mechanism of rivastigmine''s action is unknown, it is postulated to exert its therapeutic effect by enhancing cholinergic function. This is accomplished by increasing the concentration of acetylcholine through reversible inhibition of its hydrolysis by cholinesterase. If this proposed mechanism is correct, rivastigmine''s effect may lessen as the disease progresses and fewer cholinergic neurons remain functionally intact.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24385, 'Oxaprin-misr 600 mg 10 tab.', 'اوكسابرين مصر 600مجم 10 اقراص', '16', NULL, 'Nsaid.propionic acid derivatives', 'Oxaprozin', 'Misr', 'Tab', 'أقراص', '600 mg', '1', NULL, 'about oxaprozin propionic acid derivative nsaid antiarthritic. mechanism of action of oxaprozin oxaprozin is a propionic acid derivative nsaid.anti-inflammatory effects of oxaprozin are believed to be due to inhibition of cylooxygenase in platelets which', NULL, NULL, 907, '2022-08-17', 'Used to relieve the inflammation, swelling, stiffness, and joint pain associated with rheumatoid arthritis and osteoarthritis.', 'Anti-inflammatory effects of Oxaprozin are believed to be due to inhibition of cylooxygenase in platelets which leads to the blockage of prostaglandin synthesis. Antipyretic effects may be due to action on the hypothalamus, resulting in an increased peripheral blood flow, vasodilation, and subsequent heat dissipation. Oxaprozin is a non-selective NSAID, with a cell assay system showing lower COX-2 selectivity implying higher COX-1 selectivity.   ', 'Oxaprozin is a nonsteroidal anti-inflammatory drug (NSAID) with analgesic and antipyretic properties. Oxaprozin is used to treat rheumatoid arthritis, osteoarthritis, dysmenorrhea, and to alleviate moderate pain.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21082, 'Ivatherm micellar lotion 250 ml', 'ايفاثيرم ميسيلار غسول 250 مل', '229', NULL, 'Skin care', NULL, 'Ivatherm srl > vamer pharma', 'Lotion', 'لوشن', '250 ml', '1', NULL, 'micellar lotion face and eyes sensitive skin 250ml gentle cleanser for face eyes and lips made with a new technology ï- micelles which cleans soothes and refreshes even the most sensitive skin or skin with major difficulties: seborrheic dermatitis rosacea', NULL, NULL, 608, '2022-08-03', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13623, 'Albendazole 200mg/5ml susp. 30ml', 'البيندازول200مجم/5مل معلق 30 مل', '24', '14.5', 'Anthelmintic', 'Albendazole', 'Pharma cure pharmaceuticals', 'Susp', 'معلق', '200mg', '1', '1- علاج طارد للديدان للتخلص من ديدان البطن.', 'indication: this medication is an anthelmintic prescribed for tapeworm infections hydatid cyst disease cysticercosis or neurocysticercosis capillariasis cutaneous larva migrans giardiasis microsporidiosis including septata intestinalis infection intestina', '6224000651152', NULL, 5619, '2024-06-11', 'For the treatment of parenchymal neurocysticercosis due to active lesions caused by larval forms of the pork tapeworm, <i>Taenia solium</i> and for the treatment of cystic hydatid disease of the liver, lung, and peritoneum, caused by the larval form of the dog tapeworm, <i>Echinococcus granulosus</i>.', 'Albendazole causes degenerative alterations in the tegument and intestinal cells of the worm by diminishing its energy production, ultimately leading to immobilization and death of the parasite. It works by binding to the colchicine-sensitive site of tubulin, thus inhibiting its polymerization or assembly into microtubules. As cytoplasmic microtubules are critical in promoting glucose uptake in larval and adult stages of the susceptible parasites, the glycogen stores of the parasites are depleted. Degenerative changes in the endoplasmic reticulum, the mitochondria of the germinal layer, and the subsequent release of lysosomes result in decreased production of adenosine triphosphate (ATP), which is the energy required for the survival of the helminth. ', 'Albendazole is a broad-spectrum anthelmintic. The principal mode of action for albendazole is by its inhibitory effect on tubulin polymerization which results in the loss of cytoplasmic microtubules.', 'DrugBank', 0, 0, 0);
