INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Care roota hand wash spray 100 ml', 15877, 'كير روتا بخاخ غسول اليدين 100 مل', '30', NULL, 'Antiseptic', 'Antiseptic', NULL, 'Ethyl alcohol 70%', 'Dreams el habboba', 'Spray', 'بخاخ', '100 ml', '1', NULL, NULL, 'skin soothing spray ethyl alcohol 70% contains ingredients with antimicrobial properties and skin moisturizing effect.', NULL, 871, '2022-07-20', NULL, 0, 'Investigated for use/treatment in burns and burn infections, cardiac surgery, inflammatory disorders (unspecified), ischemic reperfusion injury, and sepsis and septicemia.', 'CTI-01 inhibits the systemic release of cytokines, such as TNF-alpha and HMGB1, which promote the body''s inflammatory response. The over-expression of these cytokines has been linked to diseases that occur in critical care settings, such as severe organ damage following cardiopulmonary bypass (CPB) and post-operative ileus following abdominal surgery.', NULL, 'DrugBank');
