INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Zedotefen 1mg 20 tab.', 12790, 'زيدوتيفين 1مجم 20 قرص', '9', NULL, 'Antihistamine.second-generation', 'Antihistamine.second-generation', NULL, 'Ketotifen', 'El nasr', 'Tab', 'أقراص', '1mg', '2', NULL, NULL, 'about ketotifen second generation h1 antagonist mast cell stabilizer antihistamine. mechanism of action of ketotifen it is a mast cell stabilizer. this inhibit the development of airway hyper reactivity (associated with activation of platelets by paf (pla', '6221067004389', 1012, '2023-06-07', NULL, 0, 'Administered orally, ketotifen is indicated as an add-on medication in the chronic treatment of mild atopic asthma in children.[L32283] It is also available as an over-the-counter ophthalmic solution which is indicated for the temporary prevention of itching of the eye due to allergic conjunctivitis.[L32278]', 'The precise mechanism(s) through which ketotifen exerts its therapeutic effects are unclear. Ketotifen is a potent and non-competitive antagonist of H1 histamine receptors, which is likely to be a significant contributor to its anti-allergic activity.[L32283] In addition, ketotifen stabilizes mast cells and has demonstrated _in vitro_ the ability to inhibit the release of allergic and inflammatory mediators such as histamine, leukotrienes C<sub>4</sub> and D<sub>4</sub> (i.e. SRS-A), and platelet-activating factor (PAF).[L32283]



Other _in vivo_ observations thought to contribute to ketotifen''s efficacy in asthma include the inhibition of various PAF-mediated processes (e.g. airway hyperreactivity, eosinophil and platelet accumulation in the airways), prevention of leukotriene-induced bronchoconstriction, and suppression of eosinophil priming.[L32283]', 'Ketotifen is a non-competitive histamine antagonist and mast cell stabilizer.[L32278] Administered orally, it functions as a non-bronchodilator antiasthmatic drug by inhibiting the effects of endogenous substances known to be inflammatory mediators.[L32283] While effects can take 6 to 12 weeks to become apparent,[A231214] the use of ketotifen has been demonstrated to reduce the frequency, severity, and duration of asthma symptoms, and may allow for a reduction in the use of other asthma therapies.[L32283]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Nasoflutin nasal spray 120 doses', 23276, 'نازوفلوتين بخاخ للانف 120 جرعة', '90', '58.5', 'Bronchodilator.anti-inflammatory.glucocorticoid steroid', 'Bronchodilator.anti-inflammatory.glucocorticoid steroid', NULL, 'Fluticasone furoate', 'European egyptian pharm. ind.', 'Spray', 'بخاخ', NULL, '1', 'مضاد للحساسية ومضاد للالتهاب', NULL, 'about fluticasone synthetic topical glucocorticoid anti inflammatory and antipruritic in corticosteroid-responsive dermatoses; atopic dermatitis. mechanism of action of fluticasone it reduces prostaglandin synthesis by inhibiting cyclo oxygenase enzymes b', '6221076160793', 16207, '2024-09-18', NULL, 0, 'Fluticasone propionate is indicated as an inhaler for the treatment and management of asthma by prophylaxis[FDA Label]as well as inflammatory and pruritic dermatoses[F4355]. Fluticasone propionate nasal spray is indicated for managing allergic and nonallergic rhinitis[L8309,F4358].', 'Fluticasone propionate works through an unknown mechanism to  affect the action of various cell types and mediators of inflammation[F4358]. Fluticasone propionate activates glucocorticoid receptors and inhibits lung eosinophilia in rats[F4358,F4355].', 'Systemically, fluticasone propionate activates glucocorticoid receptors, and inhibits lung eosinophilia in rats[F4358][FDA Label]. Fluticasone propionate as a topical formulation is also associated with vasoconstriction in the skin[F4355,A177118].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Aprepemic 80 mg 2 caps.', 14322, 'ابريبيمك 80 مجم 2 كبسولة', '75', NULL, 'Antiemetic', 'Antiemetic', NULL, 'Aprepitant', 'Eimc', 'Cap', 'كبسولة', '80 mg', '1', NULL, NULL, NULL, NULL, 889, '2022-08-05', NULL, 0, 'For the prevention of nausea and vomiting associated with highly emetogenic cancer chemotherapy, including high-dose cisplatin (in combination with other antiemetic agents).', 'Aprepitant has been shown in animal models to inhibit emesis induced by cytotoxic chemotherapeutic agents, such as cisplatin, via central actions. Animal and human Positron Emission Tomography (PET) studies with Aprepitant have shown that it crosses the blood brain barrier and occupies brain NK1 receptors. Animal and human studies show that Aprepitant augments the antiemetic activity of the 5-HT<sub>3</sub>-receptor antagonist ondansetron and the corticosteroid

ethasone and inhibits both the acute and delayed phases of cisplatin induced emesis.', 'Aprepitant, an antiemetic, is a substance P/neurokinin 1 (NK1) receptor antagonist which, in combination with other antiemetic agents, is indicated for the prevention of acute and delayed nausea and vomiting associated with initial and repeat courses of highly emetogenic cancer chemotherapy. Aprepitant is a selective high-affinity antagonist of human substance P/neurokinin 1 (NK1) receptors. Aprepitant has little or no affinity for serotonin (5-HT<sub>3</sub>), dopamine, and corticosteroid receptors, the targets of existing therapies for chemotherapy-induced nausea and vomiting (CI NV).', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Vocodam ear drop 15 ml', 7511, 'فوكودام نقط اذن 15 مل', '35', NULL, 'Soothing', 'Soothing', NULL, 'Hydrogen peroxide+urea+tea tree oil+garlic oil+coconut oil+panthnol+glycerin', 'Dam pharm', 'Drop', 'نقط', '15 ml', '1', NULL, NULL, NULL, NULL, 1421, '2025-09-23', NULL, 0, 'Indicated to be used as a disinfectant and sterilizer. ', 'The production of free hydroxyl radicals in the Fenton reaction is thought to be the basis of biocidal actions of hydrogen peroxide. Free radicals eventually lead to oxidative damage proteins and membrane lipids _in vivo_ [A32371]. The oxidizing radical as the ferryl radical induces DNA oxidation [A32371].', 'Hydrogen peroxide exhibits antimicrobial properties against most forms of microorganisms, including dormant forms with known high resistance profiles, such as bacterial spores and protozoal cysts. It acts as an oxidative biocide to generate free radical species to induce DNA, protein and membrane lipid damage via oxidation. ', 'DrugBank');
