INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Carfalone 25mg 20 f.c. tabs', 2098, 'كارفالون 25 مجم 20 قرص', '132', '98.5', 'Aldosterone receptor antagonist', 'Aldosterone receptor antagonist', NULL, 'Eplerenone', 'Marcyrl co.', 'Tab', 'أقراص', '25mg', '2', NULL, NULL, NULL, '6223003579395', 2036, '2025-06-18', NULL, 0, 'For improvement of survival of stable patients with left ventricular systolic dysfunction (ejection fraction <40%) and clinical evidence of congestive heart failure after an acute myocardial infarction.', 'Eplerenone binds to the mineralocorticoid receptor and thereby blocks the binding of aldosterone (component of the renin-angiotensin-aldosterone-system, or RAAS). Aldosterone synthesis, which occurs primarily in the adrenal gland, is modulated by multiple factors, including angiotensin II and non-RAAS mediators such as adrenocorticotropic hormone (ACTH) and potassium. Aldosterone binds to mineralocorticoid receptors in both epithelial (e.g., kidney) and nonepithelial (e.g., heart, blood vessels, and brain) tissues and increases blood pressure through induction of sodium reabsorption and possibly other mechanisms.', 'Eplerenone, an aldosterone receptor antagonist similar to spironolactone, has been shown to produce sustained increases in plasma renin and serum aldosterone, consistent with inhibition of the negative regulatory feedback of aldosterone on renin secretion. The resulting increased plasma renin activity and aldosterone circulating levels do not overcome the effects of eplerenone. Eplerenone selectively binds to recombinant human mineralocorticoid receptors relative to its binding to recombinant human glucocorticoid, progesterone and androgen receptors.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Anapental 500mg i.v. 1 vial', 718, 'انابنتال 500مجم للحقن الوريدي 1 فيال', '9', NULL, 'Anesthesia', 'Anesthesia', NULL, 'Thiopental', 'Sigma', 'Vial', 'فيال', '500mg', '1', 'تخدير', NULL, NULL, '6221172000115', 1095, '2022-12-02', NULL, 0, 'For use as the sole anesthetic agent for brief (15 minute) procedures, for induction of anesthesia prior to administration of other anesthetic agents, to supplement regional anesthesia, to provide hypnosis during balanced anesthesia with other agents for analgesia or muscle relaxation, for the control of convulsive states during or following inhalation anesthesia or local anesthesia, in neurosurgical patients with increased intracranial pressure, and for narcoanalysis and narcosynthesis in psychiatric disorders.', 'Thiopental binds at a distinct binding site associated with a Cl<sup>-</sup> ionopore at the GABA<sub>A</sub> receptor, increasing the duration of time for which the Cl<sup>-</sup> ionopore is open. The post-synaptic inhibitory effect of GABA in the thalamus is, therefore, prolonged.', 'Thiopental, a barbiturate, is used for the induction of anesthesia prior to the use of other general anesthetic agents and for induction of anesthesia for short surgical, diagnostic, or therapeutic procedures associated with minimal painful stimuli. Thiopental is an ultrashort-acting depressant of the central nervous system which induces hypnosis and anesthesia, but not analgesia. It produces hypnosis within 30 to 40 seconds of intravenous injection. Recovery after a small dose is rapid, with some somnolence and retrograde amnesia. Repeated intravenous doses lead to prolonged anesthesia because fatty tissues act as a reservoir; they accumulate Pentothal in concentrations 6 to 12 times greater than the plasma concentration, and then release the drug slowly to cause prolonged anesthesia', 'DrugBank');
