INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mapostin 1000mg 20 tablets', 5170, 'مابوستين 1000مجم 20 قرص', '60', NULL, 'Supplement', 'Supplement', NULL, 'Calcium carbonate 1000mg+magnesium+vitamin d3+boron+zinc+selenium+manganese+copper', 'Mapco', 'Tablet', 'أقراص', '1000mg', '2', NULL, NULL, NULL, NULL, 1142, '2022-12-12', NULL, 0, 'Calcium acetate is one of a number of calcium salts used to treat hyperphosphatemia (too much phosphate in the blood) in patients with kidney disease.', 'Calcium acetate and other calcium salts are phosphate binders. They work by binding with the phosphate in the food you eat, so that it is eliminated from the body without being absorbed.', 'Patients with advanced renal insufficiency (creatinine clearance less than 30 ml/min) exhibit phosphate retention and some degree of hyperphosphatemia. The retention of phosphate plays a pivotal role in causing secondary hyperparathyroidism associated with osteodystrophy, and soft-tissue calcification. The mechanism by which phosphate retention leads to hyperparathyroidism is not clearly delineated. Therapeutic efforts directed toward the control of hyperphosphatemia include reduction in the dietary intake of phosphate, inhibition of absorption of phosphate in the intestine with phosphate binders, and removal of phosphate from the body by more efficient methods of dialysis. The rate of removal of phosphate by dietary manipulation or by dialysis is insufficient. Dialysis patients absorb 40% to 80% of dietary phosphorus. Therefore, the fraction of dietary phosphate absorbed from the diet needs to be reduced by using phosphate binders in most renal failure patients on maintenance dialysis. Calcium acetate when taken with meals combines with dietary phosphate to form insoluble calcium phosphate which is excreted in the feces. Maintenance of serum phosphorus below 6.0 mg/dl is generally considered as a clinically acceptable outcome of treatment with phosphate binders. Calcium acetate is highly soluble at neutral pH, making the calcium readily available for binding to phosphate in the proximal small intestine.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Durjoy 30mg 3 f.c.tablets', 17979, 'ديورجوي 30مجم 3 اقراص', '80', NULL, 'Delay action', 'Delay action', NULL, 'Dapoxetine', 'Jedco int. co. for pharmaceuticals > hikma pharma', 'Tablet', 'أقراص', '30mg', '3', 'للتأخير للرجال', NULL, NULL, NULL, 3346, '2023-01-11', NULL, 0, 'For the treatment of premature ejaculation.', 'The drug''s mechanism of action is thought to be related to inhibition of neuronal reuptake of serotonin and subsequent potentiation of serotonin activity. The central ejaculatory neural circuit comprises spinal and cerebral areas that form a highly interconnected network. The sympathetic, parasympathetic, and somatic spinal centers, under the influence of sensory genital and cerebral stimuli integrated and processed at the spinal cord level, act in synergy to command physiologic events occurring during ejaculation. Experimental evidence indicates that serotonin (5-HT), throughout brain descending pathways, exerts an inhibitory role on ejaculation. To date, three 5-HT receptor subtypes (5-HT(1A), 5-HT(1B), and 5-HT(2C)) have been postulated to mediate 5-HT''s modulating activity on ejaculation.', 'Dapoxetine is a selective serotonin reuptake inhibitor currently undergoing trials through Alza (under license from GenuPro, a collaboration between Eli Lilly and PPD). Dapoxetine is a short-acting SSRI drug currently being considered for approval by the Food and Drug Administration (FDA) for the treatment of premature ejaculation in men, which would make it the first drug approved for such treatment. Despite two clinical trials finished in 2006, experts doubt it will be approved by the FDA soon because SSRIs come with undesirable side-effects after long-term use, such as psychiatric problems, dermatological reactions, increase in body weight, lower sex-drive, nausea, headache, upset stomach and weakness, thus not significantly outweighing the benefit of premature ejaculation medication versus the risks. By contrast with SSRIs approved for depression, which take 2 weeks or longer to reach steady-state concentration, dapoxetine has a unique pharmacokinetic profile, with a short time to maximum serum concentration (about 1 h) and rapid elimination (initial half-life of 1-2 h).', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Orelox 8mg/ml oral susp.', 24244, 'اوريلوكس 8مجم/مل معلق بالفم', '32', NULL, 'Antibiotic.cephalosporin.third-generation', 'Antibiotic.cephalosporin.third-generation', NULL, 'Cefpodoxime', 'Aventis pharma', 'Susp', 'معلق', '8mg', '1', NULL, NULL, 'about cefpodoxime third generation cephalosporin antibiotic oral antibiotic. mechanism of action of cefpodoxime cefpodoxime is a 3rd generation cephalosporin which exerts its bactericidal action mainly against gram positive organisms by inhibiting bacteri', NULL, 1085, '2022-06-18', NULL, 0, 'Indicated for the treatment of patients with mild to moderate infections caused by susceptible strains of the designated microorganisms.', 'Cefpodoxime is active against a wide spectrum of Gram-positive and Gram-negative bacteria. Cefpodoxime is stable in the presence of beta-lactamase enzymes. As a result, many organisms resistant to penicillins and cephalosporins, due to their production of beta-lactamase, may be susceptible to cefpodoxime. Cefpodoxime is inactivated by certain extended spectrum beta-lactamases. The bactericidal activity of cefpodoxime results from its inhibition of cell wall synthesis. The active metabolite of cefpodoxime binds preferentially to penicillin binding protein 3, which inhibits production of peptidoglycan, the primary constituent of bacterial cell walls.', 'Cefpodoxime is shown to be effective against most Gram positive and Gram negative bacteria, except <i>Pseudomonas aeruginosa</i>, <i>Enterococcus</i>, and <i>Bacteroides fragilis</i>.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Bi-alcofan 150 mg 30 tabs.', 1498, 'باي-الكوفان 150مجم 30 قرص', '81', '55.5', 'Nsaid.propionic acid derivatives', 'Nsaid.propionic acid derivatives', NULL, 'Ketoprofen', 'Alexandria', 'Tab', 'أقراص', '150 mg', '3', 'مسكن وخافض للحرارة ومضاد للالتهابات', NULL, NULL, '6222010611234', 26368, '2024-05-27', NULL, 0, 'For symptomatic treatment of acute and chronic rheumatoid arthritis, osteoarthritis, ankylosing spondylitis, primary dysmenorrhea and mild to moderate pain associated with musculotendinous trauma (sprains and strains), postoperative (including dental surgery) or postpartum pain.', 'The anti-inflammatory effects of ketoprofen are believed to be due to inhibition cylooxygenase-2 (COX-2), an enzyme involved in prostaglandin synthesis via the arachidonic acid pathway. This results in decreased levels of prostaglandins that mediate pain, fever and inflammation. Ketoprofen is a non-specific cyclooxygenase inhibitor and inhibition of COX-1 is thought to confer some of its side effects, such as GI upset and ulceration. Ketoprofen is thought to have anti-bradykinin activity, as well as lysosomal membrane-stabilizing action. Antipyretic effects may be due to action on the hypothalamus, resulting in an increased peripheral blood flow, vasodilation, and subsequent heat dissipation. ', 'Ketoprofen is a nonsteroidal anti-inflammatory agent (NSAIA) with analgesic and antipyretic properties. Ketoprofen has pharmacologic actions similar to those of other prototypical NSAIDs, which inhibit prostaglandin synthesis. Ketoprofen is used to treat rheumatoid arthritis, osteoarthritis, dysmenorrhea, and alleviate moderate pain.', 'DrugBank');
