INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Orelox 100mg 10 f.c. tab.', 24243, 'اوريلوكس 100مجم 10 اقراص', '60', NULL, 'Antibiotic.cephalosporin.third-generation', 'Antibiotic.cephalosporin.third-generation', NULL, 'Cefpodoxime', 'Sanofi', 'Tab', 'أقراص', '100mg', '1', NULL, NULL, 'about cefpodoxime third generation cephalosporin antibiotic oral antibiotic. mechanism of action of cefpodoxime cefpodoxime is a 3rd generation cephalosporin which exerts its bactericidal action mainly against gram positive organisms by inhibiting bacteri', NULL, 809, '2022-07-27', NULL, 0, 'Indicated for the treatment of patients with mild to moderate infections caused by susceptible strains of the designated microorganisms.', 'Cefpodoxime is active against a wide spectrum of Gram-positive and Gram-negative bacteria. Cefpodoxime is stable in the presence of beta-lactamase enzymes. As a result, many organisms resistant to penicillins and cephalosporins, due to their production of beta-lactamase, may be susceptible to cefpodoxime. Cefpodoxime is inactivated by certain extended spectrum beta-lactamases. The bactericidal activity of cefpodoxime results from its inhibition of cell wall synthesis. The active metabolite of cefpodoxime binds preferentially to penicillin binding protein 3, which inhibits production of peptidoglycan, the primary constituent of bacterial cell walls.', 'Cefpodoxime is shown to be effective against most Gram positive and Gram negative bacteria, except <i>Pseudomonas aeruginosa</i>, <i>Enterococcus</i>, and <i>Bacteroides fragilis</i>.', 'DrugBank');
