INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ponvory 2 to 10 mg 14 f.c.tabs. (treatment-initiation pack)', 22620, 'بونفوري 2 الى 10 مجم 14 قرص', '9,000', NULL, 'Immunosuppressant', 'Immunosuppressant', NULL, 'Ponesimod', 'Janssen cilag', 'Tab', 'أقراص', '10 mg', '2', NULL, NULL, NULL, NULL, 679, '2023-05-04', NULL, 0, 'Ponesimod is indicated to treat adults with relapsing forms of multiple sclerosis, including clinically isolated syndrome, relapsing-remitting disease, and active secondary progressive disease.[L32709]', 'The sphingosine 1-phosphate receptor 1 (S1P1R) is expressed on the surface of lymphocytes and detects sphingosine 1-phosphate (S1P) at nanomolar concentrations.[A231979] S1P is a metabolite of the cell membrane component, sphingomyelin.[A231979] As sphingomyelin degrades, lymphocytes respond to agonism of S1P1R by concentration gradients of S1P.[A231979] Lymphocytes leave the lymphoid organs in response to higher concentrations of S1P in blood and lymph.[A231979] Ponesimod modulates this response by stimulating and internalizing S1P1R on lymphocytes, effectively blinding them to concentration gradients of S1P, reducing the number of lymphocytes in blood.[A231979,L32709] Ponesimod is roughly 650 times more selective for S1P1R than S1P.[A231979]', 'Ponesimod is a sphingosine 1-phosphate receptor 1 modulator indicated to treat adults with relapsing forms of multiple sclerosis.[L32709] It has a long duration of action as it is given once daily.[L32709] Patients should be counselled about the risk of infections, bradyarrhythmia, atrioventricular conduction delays, decreased respiratory function, liver injury, increased blood pressure, cutaneous malignancies, fetal harm, and macular edema.[L32709]', 'DrugBank');
