INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27382, 'Speczil 250mg/5ml susp. 60 ml', 'سبيكزيل 250مجم/5مل شراب معلق 60مل', '21', NULL, 'Antibiotic.cephalosporin.second-generation', 'Cefprozil', 'Adwia', 'Susp', 'معلق', '250mg', '1', NULL, 'about cefprozil second generation cephalosporin antibiotic oral antibiotic. mechanism of action of cefprozil cefprozil is a short acting 2nd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms', NULL, NULL, 976, '2022-07-23', 0, 'For the treatment of the following infections (respiratory, skin, soft tissue, UTI, ENT) caused by; S. pneumoniae, H. influenzae, staphylococci, S. pyogenes (group A beta-hemolytic streptococci), E. coli, P. mirabilis, Klebsiella sp, coagulase-negative staph', 'Cefprozil, like the penicillins, is a beta-lactam antibiotic. By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, it inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that cefprozil interferes with an autolysin inhibitor.', 'Cefprozil, a semisynthetic, second-generation cephalosporin, is used to treat otitis media, soft-tissue infections, and respiratory tract infections.', 'DrugBank', 0, 0, 0);
