INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14335, 'Aptoful 10/25mg 10 f.c.tab', 'ابتوفول 10/25 مجم 10 اقراص', '9', NULL, 'Antihypertensive.combined beta blocker with diuretic', 'Bisoprolol fumarate+hydrochlorothiazide', 'Eipico', 'Tab', 'أقراص', '25mg', '1', NULL, 'indications for bisoprolol + hydrochlorothiazide it is the combination of beta blocker bisoprolol and diuretic hydrochlorothiazide. hypertention typical dosage for bisoprolol + hydrochlorothiazide 1 tablet / day pregnancy related information contraindicat', NULL, NULL, 827, '2022-07-18', 'Bisoprolol is indicated for the treatment of mild to moderate hypertension.[L7219] It may be used off-label to treat heart failure, atrial fibrillation, and angina pectoris.[A180460,A180463]', 'Though the mechanism of action of bisoprolol has not been fully elucidated in hypertension, it is thought that therapeutic effects are achieved through the antagonism of β-1adrenoceptors to result in lower cardiac output. Bisoprolol is a competitive, cardioselective β1-adrenergic antagonist. When β1-receptors (located mainly in the heart)[T640] are activated by adrenergic neurotransmitters such as epinephrine, both the blood pressure and heart rate increase, leading to greater cardiovascular work, increasing the demand for oxygen.  Bisoprolol reduces cardiac workload by decreasing contractility and the need for oxygen through competitive inhibition of β1-adrenergic receptors.[A180502,T640]



Bisoprolol is also thought to reduce the output of renin in the kidneys, which normally increases blood pressure. Additionally, some central nervous system effects of bisoprolol may include diminishing sympathetic nervous system output from the brain, decreasing blood pressure and heart rate.[L7219]', 'Bisoprolol decreases heart rate (chronotropy), decreases contractility (inotropy), and reduces blood pressure.[T640,L7219] The results of various clinical studies indicate that bisoprolol reduces cardiovascular mortality and all-cause mortality in patients with heart failure and decreased cardiac ejection fraction (EF).[A180466,A180505] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30005, 'Bonefit 5000 i.u. 30 tabs', 'بونفيت 5000 وحدة دولية 30 قرص', '90', '70', 'Vitamin d', 'Cholecalciferol(vitamin d3)', 'Organix > rotabogen', 'Tab', 'أقراص', NULL, '3', 'فيتامين دال', NULL, NULL, NULL, 2168, '2023-09-18', NULL, NULL, NULL, NULL, 0, 0, 0);
