INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30271, 'Carbetrade 100mcg/ml 5 amp.for l.v & l.m inj.', 'كاربيتراد 100مكجم/مل 5 امبول حقن عضل.وريد', '630', '357.5', 'Antihemorrhagic and uterotonic drug', 'Carbetocin 100mcg', 'Eipico > philo pharm pharmaceuticals', 'Inj', 'حقن', '100mcg', '5', 'يستخدم للوقاية من وهن الرحم، والنزيف المحتمل حدوثه بعد العمليات القيصرية التي أجريت تحت التخدير.', 'carbetrade ampoule (solution for iv&im injection) is indicated for the prevention of postpartum haemorrhage due to uterine atony', NULL, NULL, 1310, '2024-12-29', 'Used to control postpartum hemorrhage and bleeding after giving birth.', 'Carbetocin binds to oxytocin receptors present on the smooth musculature of the uterus, resulting in rhythmic contractions of the uterus, increased frequency of existing contractions, and increased uterine tone. The oxytocin receptor content of the uterus is very low in the non-pregnant state, and increases during pregnancy, reaching a peak at the time of delivery.', 'Carbetocin is a drug used to control postpartum hemorrhage, bleeding after giving birth. It is sold under the trade name Duratocin. It is an analogue of oxytocin, and its action is similar to that of oxytocin; it causes contraction of the uterus.', 'DrugBank', 0, 0, 0);
