INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6486, 'Flibanoact 100mg 28 f.c. tabs', 'فليبانواكت 100مجم 28 قرص', '560', NULL, 'Women need', 'Flibanserin', 'Aug pharma', 'Tab', 'أقراص', '100mg', '2', 'مقوى للسيدات', NULL, NULL, NULL, 1598, '2023-02-16', 'For the treatment of hypoactive sexual desire disorder (HSDD) in premenopausal women. ', 'Flibansetrin has high affinity for serotonin receptors in the brain: it acts as an agonist on 5-HT1A and an antagonist on 5-HT2A. In vivo, flibanserin binds equally to 5-HT1A and 5-HT2A receptors. However, under higher levels of brain 5-HT (i.e., under stress), flibanserin may occupy 5-HT2A receptors in higher proportion than 5-HT(1A) receptors. It may also moderately antagonize D4 (dopamine) receptors and 5-HT2B and 5-HTB2C. Its action on neurotransmitter receptors may contribute to reduction in serotonin levels and increase in dopamine and norepinephrine levels, all of which may play part in reward processing. ', NULL, 'DrugBank', 0, 0, 0);
