INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (24947, 'Piojet 30mg 10 tab.', 'بيوجت 30مجم 10 اقراص', '37', NULL, 'Anti-diabetic.sensitizers.glitazone', 'Pioglitazone', 'European egyptian pharm. ind.', 'Tab', 'أقراص', '30mg', '1', NULL, 'about pioglitazone thiazolidinedione (tzd) derivative insulin sensitizer oral anti diabetic. mechanism of action of pioglitazone the drug exerts antidiabetic action by acting as a selective agonist for the nuclear peroxisome proliferator-activated recepto', '6221076010784', NULL, 734, '2022-12-08', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Pioglitazone is an antihyperglycemic used as an adjunct to diet, exercise, and other antidiabetic medications to manage type 2 diabetes mellitus.[L11416,L11419,L11422,L11425] It is administered as a racemic mixture, though there is no pharmacologic difference between the enantiomers and they appear to interconvert _in vivo_ with little consequence.[L11416] The thiazolidinedione class of medications, which also includes [rosiglitazone] and [troglitazone], exerts its pharmacological effect primarily by promoting insulin sensitivity and the improved uptake of blood glucose[L11416] via agonism at the peroxisome proliferator-activated receptor-gamma (PPARγ).[A19757] PPARs are ligand-activated transcription factors that are involved in the expression of more than 100 genes and affect numerous metabolic processes, most notably lipid and glucose homeostasis.[A19759]

Thiazolidinediones, including pioglitazone, have fallen out of favor in recent years due to the presence of multiple adverse effects and warnings regarding their use (e.g. congestive heart failure, bladder cancer) and the availability of safer and more effective alternatives for patients with type 2 diabetes mellitus.[L11461]', '["A10BD09", "A10BG03", "A10BD05", "A10BD06", "A10BD12"]', '[]');
