INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25236, 'Primacor 10 mg/10 ml 10 i.v. amps. (illegal import)', 'بريماكور 10مجم/10مل 10 امبول وريد', '0', NULL, NULL, 'Milrinone', 'Sanofi aventis >', 'Amp', 'أمبول', '10 mg', '10', NULL, 'primacor is a vasodilator that works by relaxing the muscles in your blood vessels to help them dilate (widen). this lowers blood pressure and allows blood to flow more easily through your veins and arteries. primacor is used as a short-term treatment for', '3582910005803', NULL, 4269, '2022-10-20', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Heart failure is a multifactorial condition that affects roughly 1-2% of the adult population. Often the result of long-term myocardial ischemia, cardiomyopathy, or other cardiac insults, heart failure results from an inability of the heart to perfuse peripheral tissues with sufficient oxygen and metabolites, resulting in complex systemic pathologies. Heart failure is underpinned by numerous physiological changes, including alteration in β-adrenergic signalling and cyclic adenosine monophosphate (cAMP) production, which affects the heart''s contractile function and cardiac output.[A228323] Milrinone is a second-generation bipyridine phosphodiesterase (PDE) inhibitor created through chemical modification of [amrinone].[A228333] As a PDE-III inhibitor, milrinone results in increased cAMP levels and improves cardiac function and peripheral vasodilation in acute decongested heart failure.[A228338, A11759, A228323, A228333, A228348, L31483]

Milrinone was originally synthesized at the Sterling Winthrop Research Institute in the 1980s.[A228333] It was approved by the FDA on December 31, 1987, and was marketed under the trademark PRIMACOR® by Sanofi-Aventis US before being discontinued.[L31483]', '["C01CE02"]', '[]');
