INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27477, 'Statergic 2.5mg/5ml ophthalmic sol. 10 ml', 'ستاتيرجك 2.5مجم/5مل قطرة للعين 10مل', '24', NULL, 'Ophthalmic antihistaminic', 'Epinastine', 'Sigma tec', 'Sol', 'محلول', '2.5mg', '1', NULL, NULL, NULL, NULL, 783, '2022-07-25', 0, 'For the prevention of itching associated with allergic conjunctivitis.', 'Epinastine has a multiaction effect that inhibits the allergic response in 3 ways: 1. stabilizes mast cells by preventing mast cell degranulation to control the allergic response, 2. prevents histamine binding to both the H1- and H<sub>2</sub>-receptors to stop itching and provide lasting protection, and 3. prevents the release of proinflammatory chemical mediators from the blood vessel to halt progression of the allergic response.', 'Epinastine is an antihistamine and an inhibitor of histamine release from the mast cell for topical administration to the eyes. Epinastine is indicated for the prevention of itching associated with allergic conjunctivitis. Epinastine is a topically active, direct H<sub>1</sub>-receptor antagonist and an inhibitor of the release of histamine from the mast cell. Epinastine is selective for the histamine H<sub>1</sub>-receptor and has affinity for the histamine H2 receptor. Epinastine also possesses affinity for the a1-, a2-, and 5-HT<sub>2</sub> -receptors. Epinastine does not penetrate the blood/brain barrier and, therefore, is not expected to induce side effects of the central nervous system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27478, 'Statergic 2.5mg/5ml ophthalmic sol. 5 ml', 'ستاتيرجك 2.5مجم/5مل قطرة للعين 5مل', '12', NULL, 'Ophthalmic antihistaminic', 'Epinastine', 'Sigma tec', 'Sol', 'محلول', '2.5mg', '1', NULL, NULL, NULL, NULL, 807, '2022-07-12', 0, 'For the prevention of itching associated with allergic conjunctivitis.', 'Epinastine has a multiaction effect that inhibits the allergic response in 3 ways: 1. stabilizes mast cells by preventing mast cell degranulation to control the allergic response, 2. prevents histamine binding to both the H1- and H<sub>2</sub>-receptors to stop itching and provide lasting protection, and 3. prevents the release of proinflammatory chemical mediators from the blood vessel to halt progression of the allergic response.', 'Epinastine is an antihistamine and an inhibitor of histamine release from the mast cell for topical administration to the eyes. Epinastine is indicated for the prevention of itching associated with allergic conjunctivitis. Epinastine is a topically active, direct H<sub>1</sub>-receptor antagonist and an inhibitor of the release of histamine from the mast cell. Epinastine is selective for the histamine H<sub>1</sub>-receptor and has affinity for the histamine H2 receptor. Epinastine also possesses affinity for the a1-, a2-, and 5-HT<sub>2</sub> -receptors. Epinastine does not penetrate the blood/brain barrier and, therefore, is not expected to induce side effects of the central nervous system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27479, 'Staticap 120mg 20 caps', 'ستاتيكاب 120مجم 20 كبسولة', '83', NULL, 'Weight loss', 'Orlistat', 'Egpi > mira international', 'Cap', 'كبسولة', '120mg', '2', 'للتخسيس وبرامج انقاص الوزن', NULL, '6224009669172', NULL, 3821, '2023-02-04', 0, 'Orlistat is indicated for obesity management including weight loss and weight maintenance when used in combination with calorie reduction in overweight and obese adults; this indication applies to both the prescription formulation of 120 mg[L11130] and the over-the-counter formulation of 60 mg.[L31963] Orlistat in the 120 mg prescription formulation is also indicated to reduce the risk of weight regain following weight loss.[L11130]', 'Orlistat is a potent and selective inhibitor of various lipase enzymes responsible for the metabolism of fat. It acts in the gastrointestinal (GI) tract via covalent binding to the serine residues located on the active site of both gastric and pancreatic lipase. When orlistat is taken with food containing fat, it partially inhibits the hydrolysis of triglycerides. This decreases absorption of monoaclglycerides and free fatty acids, contributing to weight maintenance and weight loss.[A37653,L11130]', 'Orlistat helps with weight reduction and maintenance by inhibiting the absorption of dietary fats via the inhibition of lipase enzymes.[A1734,A1735]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27480, 'Statirose 20mg 10 tab.', 'ستاتيروز 20مجم 10 اقراص', '52', '45', 'Antihyperlipidemic.statins', 'Rosuvastatin', 'October pharma > pharmagin-egypt', 'Tab', 'أقراص', '20mg', '1', NULL, 'about rosuvastatin statin hmg-coa reductase inhibitor antilipemic. mechanism of action of rosuvastatin it is a hypolipidaemic statin which competitively inhibits the conversion of 3-hydroxy-3-methyl glutaryl coenzyme a (hmg coa) to mevalonate. the drug ex', '6224009669172', NULL, 1103, '2025-11-04', 0, 'The FDA monograph states that rosuvastatin is indicated as an adjunct to diet in the treatment of triglyceridemia, Primary Dysbetalipoproteinemia (Type III Hyperlipoproteinemia), and Homozygous Familial Hypercholesterolemia.[F4649]



The Health Canada monograph for rosuvastatin further specifies that rosuvastatin is indicated for the reduction of elevated total cholesterol (Total-C), LDL-C, ApoB, the Total-C/HDL-C ratio and triglycerides (TG) and for increasing HDL-C in hyperlipidemic and dyslipidemic conditions when response to diet and exercise alone has been inadequate. It is also indicated for the prevention of major cardiovascular events (including risk of myocardial infarction, nonfatal stroke, and coronary artery revascularization) in adult patients without documented history of cardiovascular or cerebrovascular events, but with at least two conventional risk factors for cardiovascular disease.[F4652]



Prescribing of statin medications is considered standard practice following any cardiovascular events and for people with a moderate to high risk of development of CVD. Statin-indicated conditions include diabetes mellitus, clinical atherosclerosis (including myocardial infarction, acute coronary syndromes, stable angina, documented coronary artery disease, stroke, trans ischemic attack (TIA), documented carotid disease, peripheral artery disease, and claudication), abdominal aortic aneurysm, chronic kidney disease, and severely elevated LDL-C levels.[A181087, A181406]', 'Rosuvastatin is a statin medication and a competitive inhibitor of the enzyme HMG-CoA (3-hydroxy-3-methylglutaryl coenzyme A) reductase, which catalyzes the conversion of HMG-CoA to mevalonate, an early rate-limiting step in cholesterol biosynthesis.[A181421] Rosuvastatin acts primarily in the liver, where decreased hepatic cholesterol concentrations stimulate the upregulation of hepatic low density lipoprotein (LDL) receptors which increases hepatic uptake of LDL. Rosuvastatin also inhibits hepatic synthesis of very low density lipoprotein (VLDL).[F4649] The overall effect is a decrease in plasma LDL and VLDL. 



In vitro and in vivo animal studies also demonstrate that rosuvastatin exerts vasculoprotective effects independent of its lipid-lowering properties, also known as the pleiotropic effects of statins.[A181424] This includes improvement in endothelial function, enhanced stability of atherosclerotic plaques, reduced oxidative stress and inflammation, and inhibition of the thrombogenic response.



Statins have also been found to bind allosterically to β2 integrin function-associated antigen-1 (LFA-1), which plays an important role in leukocyte trafficking and in T cell activation.[A181559]



Rosuvastatin exerts an anti-inflammatory effect on rat mesenteric microvascular endothelium by attenuating leukocyte rolling, adherence and transmigration.[A1807] The drug also modulates nitric oxide synthase (NOS) expression and reduces ischemic-reperfusion injuries in rat hearts.[A1787] Rosuvastatin increases the bioavailability of nitric oxide[A1807,A1799,A1787] by upregulating NOS[A1791] and by increasing the stability of NOS through post-transcriptional polyadenylation.[A1796] It is unclear as to how rosuvastatin brings about these effects though they may be due to decreased concentrations of mevalonic acid.', 'Rosuvastatin is a synthetic, enantiomerically pure antilipemic agent. It is used to lower total cholesterol, low density lipoprotein-cholesterol (LDL-C), apolipoprotein B (apoB), non-high density lipoprotein-cholesterol (non-HDL-C), and trigleride (TG) plasma concentrations while increasing HDL-C concentrations. High LDL-C, low HDL-C and high TG concentrations in the plasma are associated with increased risk of atherosclerosis and cardiovascular disease. The total cholesterol to HDL-C ratio is a strong predictor of coronary artery disease and high ratios are associated with higher risk of disease. Increased levels of HDL-C are associated with lower cardiovascular risk. By decreasing LDL-C and TG and increasing HDL-C, rosuvastatin reduces the risk of cardiovascular morbidity and mortality.[A181087, A181406]



Elevated cholesterol levels, and in particular, elevated low-density lipoprotein (LDL) levels, are an important risk factor for the development of CVD.[A181087] Use of statins to target and reduce LDL levels has been shown in a number of landmark studies to significantly reduce the risk of development of CVD and all-cause mortality.[A181090,A181093,A181096,A181427,A181475] Statins are considered a cost-effective treatment option for CVD due to their evidence of reducing all-cause mortality including fatal and non-fatal CVD as well as the need for surgical revascularization or angioplasty following a heart attack.[A181087, A181406] Evidence has shown that even for low-risk individuals (with <10% risk of a major vascular event occurring within 5 years) statins cause a 20%-22% relative reduction in major cardiovascular events (heart attack, stroke, coronary revascularization, and coronary death) for every 1 mmol/L reduction in LDL without any significant side effects or risks.[A181397, A181403]



**Skeletal Muscle Effects**



Cases of myopathy and rhabdomyolysis with acute renal failure secondary to myoglobinuria have been reported with HMG-CoA reductase inhibitors, including rosuvastatin. These risks can occur at any dose level, but are increased at the highest dose (40 mg). Rosuvastatin should be prescribed with caution in patients with predisposing factors for myopathy (e.g., age ≥ 65 years, inadequately treated hypothyroidism, renal impairment).



The risk of myopathy during treatment with rosuvastatin may be increased with concurrent administration of some other lipid-lowering therapies (such as [fenofibrate] or [niacin]), [gemfibrozil], [cyclosporine], [atazanavir]/[ritonavir], [lopinavir]/ritonavir, or [simeprevir].  Cases of myopathy, including rhabdomyolysis, have been reported with HMG-CoA reductase inhibitors, including rosuvastatin, coadministered with [colchicine], and caution should therefore be exercised when prescribing these two medications together.[F4649] 



Real-world data from observational studies has suggested that 10-15% of people taking statins may experience muscle aches at some point during treatment.[A182258]



**Liver Enzyme Abnormalities**



Increases in serum transaminases have been reported with HMG-CoA reductase inhibitors, including rosuvastatin. In most cases, the elevations were transient and resolved or improved on continued therapy or after a brief interruption in therapy. There were two cases of jaundice, for which a relationship to rosuvastatin therapy could not be determined, which resolved after discontinuation of therapy. There were no cases of liver failure or irreversible liver disease in these trials.[F4649]



**Endocrine Effects**



Increases in HbA1c and fasting serum glucose levels have been reported with HMG-CoA reductase inhibitors, including rosuvastatin calcium tablets. Based on clinical trial data with rosuvastatin, in some instances these increases may exceed the threshold for the diagnosis of diabetes mellitus.[F4649]



An in vitro study found that [atorvastatin], [pravastatin], [rosuvastatin], and [pitavastatin] exhibited a dose-dependent cytotoxic effect on human pancreas islet β cells, with reductions in cell viability of 32, 41, 34 and 29%, respectively, versus control]. Moreover, insulin secretion rates were decreased by 34, 30, 27 and 19%, respectively, relative to control.[A182012]



HMG-CoA reductase inhibitors interfere with cholesterol synthesis and lower cholesterol levels and, as such, might theoretically blunt adrenal or gonadal steroid hormone production. Rosuvastatin demonstrated no effect upon nonstimulated cortisol levels and no effect on thyroid metabolism as assessed by TSH plasma concentration. In rosuvastatin treated patients, there was no impairment of adrenocortical reserve and no reduction in plasma cortisol concentrations. Clinical studies with other HMG-CoA reductase inhibitors have suggested that these agents do not reduce plasma testosterone concentration. The effects of HMG-CoA reductase inhibitors on male fertility have not been studied. The effects, if any, on the pituitarygonadal axis in premenopausal women are unknown.[F4652]



**Cardiovascular**



Ubiquinone levels were not measured in rosuvastatin clinical trials, however significant decreases in circulating ubiquinone levels in patients treated with other statins have been observed. The clinical significance of a potential long-term statin-induced deficiency of ubiquinone has not been established. It has been reported that a decrease in myocardial ubiquinone levels could lead to impaired cardiac function in patients with borderline congestive heart failure.[F4652]



**Lipoprotein A**



In some patients, the beneficial effect of lowered total cholesterol and LDL-C levels may be partly blunted by a concomitant increase in the Lipoprotein(a) [Lp(a)] concentrations. Present knowledge suggests the importance of high Lp(a) levels as an emerging risk factor for coronary heart disease. It is thus desirable to maintain and reinforce lifestyle changes in high-risk patients placed on rosuvastatin therapy.[F4652] Further studies have demonstrated statins affect Lp(a) levels differently in patients with dyslipidemia depending on their apo(a) phenotype; statins increase Lp(a) levels exclusively in patients with the low molecular weight apo(a) phenotype.[A181418]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27481, 'Phara fero 18 plus - 20 caps.', 'فرافيرو 18 بلس - 20 كبسولة', '275', '190', 'Iron supplement', 'Iron+zinc+copper+molybdenum+vitamins(b1+b2+b3+b5+b6+b7+b8+b9+b12)+beta-carotene+vitamin c+vitamin e', 'Delta pharma > devartlab pharma', 'Cap', 'كبسولة', NULL, '1', NULL, '-ï-phara ferro 18ï-ï-plus isï-the fully reacted amino acid chelated formula. it is specially formulated for adults to support anemic patient.ï- - phara ferro 18ï-ï-plus is enriched with essential nutrients such asï-lactoferrin+iron (18 mg) zinc copper mol', '6223006935075', NULL, 1288, '2025-01-21', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27482, 'Statukast 10mg 10 scored f.c. tabs', 'ستاتوكاست 10مجم 10 اقراص', '42', '32', 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Adwia', 'Tab', 'أقراص', '10mg', '1', 'علاج أزمات الصدر وصعوبة وضيق التنفس', NULL, '6223002971817', NULL, 3867, '2024-06-13', 0, 'Montelukast is indicated for:



(a) the prophylaxis and chronic treatment of asthma in adults and pediatric patients who are 12 months of age and older[L6301], although other regional health authorities specifically note this indication for adults and adolescents who are 15 years and older[L6304,L6307] and also include indications for preventing day and night-time symptoms, and the treatment of acetylsalicylic acid-sensitive asthma[L6304];



(b) the prevention of exercise-induced bronchoconstriction (EIB) in patients who are 6 years of age and older[L6301], although other regional health authorities specifically note this indication for adults and adolescents who are 15 years and older[L6304,L6307]; and



(c) the relief of symptoms of seasonal allergic rhinitis in patients 2 years of age and older and perennial allergic rhinitis in patients 6 months of age and older[L6301], although other regional health authorities specifically note the relief of seasonal allergic rhinitis symptoms for adults and adolescents who are 15 years and older[L6304,L6307].



Furthermore, some formulations like chewable montelukast tablets may also be specifically indicated by particular regulatory bodies for the prophylaxis and chronic treatment of asthma, including the prevention of day and night-time symptoms, the treatment of acetylsalicylic acid based asthma, and the prevention of exercise-induced bronchoconstriction in adult and pediatric patients aged 2 and older[L6328], between the ages 2 and 5[L6325], or between the ages of 6 and 14 years.[L6331]



Moreover, when employed for such indications montelukast is considered effective as monotherapy or when combined with other medications indicated for the maintenance treatment of chronic asthma.[L6304,L6328] For instance, montelukast and inhaled corticosteroids can be used concomitantly to demonstrate additive effects to control asthma or to decrease the necessary inhaled corticosteroid dose while still maintaining clinical stability.[L6304,L6328]



Additionally, in patients who continue to experience asthma symptoms, montelukast can also be combined with an ''as required'' short-acting beta-agonist, an inhaled corticosteroid, or inhaled corticosteroid paired with a long-acting beta-agonist.[L6304,L6328]', 'Cysteinyl leukotrienes (CysLT) like LTC4, LTD4, and LTE4, among others, are eicosanoids released by a variety of cells like mast cells and eosinophils.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] When such CysLT bind to corresponding CysLT receptors like CysLT type-1 receptors located on respiratory airway smooth muscle cells, airway macrophages, and on various pro-inflammatory cells like eosinophils and some specific myeloid stem cells activities that facilitate the pathophysiology of asthma and allergic rhinitis are stimulated.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



In particular, CysLT-mediated airway bronchoconstriction, occluding mucous secretion, vascular permeability, and eosinophil recruitment are all types of effects that facilitate asthma.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Alternatively, in allergic rhinitis, CysLTs are released by the nasal mucosa when exposed to allergens during both early and late phase reactions and participate in eliciting symptoms of allergic rhinitis like a congested nose and airway.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



Subsequently, montelukast is a leukotriene receptor antagonist that binds with high affinity and selectivity to the CysLT type 1 receptor, which consequently assists in inhibiting any physiological actions of CysLTs like LTC4, LTD4, and LTE4 at the receptor that may facilitate asthma or allergic rhinitis.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]', 'Montelukast is a leukotriene receptor antagonist that demonstrates a marked affinity and selectivity to the cysteinyl leukotriene receptor type-1 in preference to many other crucial airway receptors like the prostanoid, cholinergic, or beta-adrenergic receptors.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] As a consequence, the agent can elicit substantial blockage of LTD4 leukotriene-mediated bronchoconstriction with doses as low as 5 mg.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Moreover, a placebo-controlled, crossover study (n=12) demonstrated that montelukast is capable of inhibiting early and late phase bronchoconstriction caused by antigen challenge by 75% and 57% respectively.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



In particular, it has been documented that montelukast can cause bronchodilation as soon as within 2 hours of oral administration.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] This action can also be additive to the bronchodilation caused by the concomitant use of a beta agonist.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Nevertheless, clinical investigations performed with adults 15 years of age and older revealed that no additional clinical benefit is obtained when doses of montelukast greater than 10 mg a day are used.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



Additionally, in clinical trials with adults and pediatric asthmatic patients aged 6 to 14 years, it was also determined that montelukast can reduce mean peripheral blood eosinophils by about 13% to 15% from baseline in comparison to placebo during double-blind treatment periods.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] At the same time, in patients aged 15 years and older who were experiencing seasonal allergic rhinitis, the use of montelukast caused a median reduction of 13% in peripheral blood eosinophil counts when compared to placebo as well.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27483, 'Statukast 4mg oral granules 10 sachets', 'ستاتوكاست 4مجم 10 اكياس', '50', '40', 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Adwia', 'Sachet', 'أكياس', '4mg', '1', NULL, 'about montelukast leukotriene(cyslt1) receptor antagonist antiinflammatory antiasthma. mechanism of action of montelukast montelukast is competitively and selectively antagonizes cyslt1 receptor (leukotrienes receptor) mediated bronchoconstriction.montelu', '6223002972081', NULL, 3306, '2024-12-23', 0, 'Montelukast is indicated for:



(a) the prophylaxis and chronic treatment of asthma in adults and pediatric patients who are 12 months of age and older[L6301], although other regional health authorities specifically note this indication for adults and adolescents who are 15 years and older[L6304,L6307] and also include indications for preventing day and night-time symptoms, and the treatment of acetylsalicylic acid-sensitive asthma[L6304];



(b) the prevention of exercise-induced bronchoconstriction (EIB) in patients who are 6 years of age and older[L6301], although other regional health authorities specifically note this indication for adults and adolescents who are 15 years and older[L6304,L6307]; and



(c) the relief of symptoms of seasonal allergic rhinitis in patients 2 years of age and older and perennial allergic rhinitis in patients 6 months of age and older[L6301], although other regional health authorities specifically note the relief of seasonal allergic rhinitis symptoms for adults and adolescents who are 15 years and older[L6304,L6307].



Furthermore, some formulations like chewable montelukast tablets may also be specifically indicated by particular regulatory bodies for the prophylaxis and chronic treatment of asthma, including the prevention of day and night-time symptoms, the treatment of acetylsalicylic acid based asthma, and the prevention of exercise-induced bronchoconstriction in adult and pediatric patients aged 2 and older[L6328], between the ages 2 and 5[L6325], or between the ages of 6 and 14 years.[L6331]



Moreover, when employed for such indications montelukast is considered effective as monotherapy or when combined with other medications indicated for the maintenance treatment of chronic asthma.[L6304,L6328] For instance, montelukast and inhaled corticosteroids can be used concomitantly to demonstrate additive effects to control asthma or to decrease the necessary inhaled corticosteroid dose while still maintaining clinical stability.[L6304,L6328]



Additionally, in patients who continue to experience asthma symptoms, montelukast can also be combined with an ''as required'' short-acting beta-agonist, an inhaled corticosteroid, or inhaled corticosteroid paired with a long-acting beta-agonist.[L6304,L6328]', 'Cysteinyl leukotrienes (CysLT) like LTC4, LTD4, and LTE4, among others, are eicosanoids released by a variety of cells like mast cells and eosinophils.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] When such CysLT bind to corresponding CysLT receptors like CysLT type-1 receptors located on respiratory airway smooth muscle cells, airway macrophages, and on various pro-inflammatory cells like eosinophils and some specific myeloid stem cells activities that facilitate the pathophysiology of asthma and allergic rhinitis are stimulated.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



In particular, CysLT-mediated airway bronchoconstriction, occluding mucous secretion, vascular permeability, and eosinophil recruitment are all types of effects that facilitate asthma.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Alternatively, in allergic rhinitis, CysLTs are released by the nasal mucosa when exposed to allergens during both early and late phase reactions and participate in eliciting symptoms of allergic rhinitis like a congested nose and airway.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



Subsequently, montelukast is a leukotriene receptor antagonist that binds with high affinity and selectivity to the CysLT type 1 receptor, which consequently assists in inhibiting any physiological actions of CysLTs like LTC4, LTD4, and LTE4 at the receptor that may facilitate asthma or allergic rhinitis.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]', 'Montelukast is a leukotriene receptor antagonist that demonstrates a marked affinity and selectivity to the cysteinyl leukotriene receptor type-1 in preference to many other crucial airway receptors like the prostanoid, cholinergic, or beta-adrenergic receptors.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] As a consequence, the agent can elicit substantial blockage of LTD4 leukotriene-mediated bronchoconstriction with doses as low as 5 mg.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Moreover, a placebo-controlled, crossover study (n=12) demonstrated that montelukast is capable of inhibiting early and late phase bronchoconstriction caused by antigen challenge by 75% and 57% respectively.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



In particular, it has been documented that montelukast can cause bronchodilation as soon as within 2 hours of oral administration.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] This action can also be additive to the bronchodilation caused by the concomitant use of a beta agonist.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Nevertheless, clinical investigations performed with adults 15 years of age and older revealed that no additional clinical benefit is obtained when doses of montelukast greater than 10 mg a day are used.[L6301,L6304,L6307,L6310,L6325,L6328,L6331]



