INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27920, 'Tarivid 200mg 10 f.c. tab.', 'تاريفيد 200مجم 10 قرص', '82', '57.75', 'Antibiotic.quinolone', 'Ofloxacin', 'Sanofi', 'Tab', 'أقراص', '200mg', '1', NULL, 'about ofloxacin a second generation fluoroquinolone(quinolones) antibiotic. mechanism of action of ofloxacin ofloxacin is a bactericidal drug. it act by inhibiting the enzyme dna gyrase (topoisomerase 2) and topoisomerase 4.dna gyrase helps in the formati', '6223003991319', NULL, 3739, '2024-01-22', 0, 'For the treatment of infections (respiratory tract, kidney, skin, soft tissue, UTI), urethral and cervical gonorrhoea.', 'Ofloxacin acts on DNA gyrase and toposiomerase IV, enzymes which, like human topoisomerase, prevents the excessive supercoiling of DNA during replication or transcription. By inhibiting their function, the drug thereby inhibits normal cell division.', 'Ofloxacin is a quinolone/fluoroquinolone antibiotic. Ofloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Ofloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria.', 'DrugBank', 0, 0, 0);
