    ) VALUES (17209, 'Delmonkast 5mg 14 chewable tabs.', 'ديلمونكاست 5 مجم 14 قرص', '38', NULL, 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Delta pharma', 'Tab', 'أقراص', '5mg', '2', NULL, 'Montelukast is indicated for:



