INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25283, 'Probactcin 2% top. cream 10 gm', 'بروباكتسين2%كريم موضعي 10جم', '37', NULL, 'Anti-bacterial.protein synthesis inhibitor', 'Mupirocin', 'Kahira > october pharma', 'Cream', 'كريم', '2%', '1', NULL, 'about mupirocin a naturally antibiotic isolated from pseudomonas fluorescens antibiotic.ssmechanism of action of mupirocinsmupirocin exerts its action by reversibly binding to isoleucyl transfer rna synthetase and inhibits the enzyme activity. this enzyme', NULL, NULL, 891, '2022-07-07', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Mupirocin, formerly termed pseudomonic acid A,[A178531] is a novel antibacterial agent with a unique chemical structure and mode of action apart from other antibiotic agents. Produced by fermentation using the organism _Pseudomonas fluorescens_, mupirocin is a naturally-occurring antibiotic that displays a broad-specturm activity against many gram-positive bacteria and certain gram-negative bacteria _in vitro_.[L10580] It primarily works by inhibiting bacterial protein synthesis. Due to its unique mode of action of inhibiting the activity of bacterial isoleucyl-tRNA synthetase, mupirocin does not demonstrate cross-resistance with other classes of antimicrobial agents, giving it a therapeutic advantage.[L10580] It is available in topical formulations only due to extensive systemic metabolism[A178531] and is used in the treatment of impetigo caused by _Staphylococcus aureus_ and _Streptococcus pyogenes_ and traumatic skin lesions due to secondary skin infections caused by _S. aureus_ and _S. pyogenes_. There is also some clinical evidence that suggests the potential role of mupirocin in eradicating nasal carriage of Staphylococci when administered intranasally.[A178531,A178591] Mupirocin is commonly marketed under the brand name Bactroban.', '["R01AX06", "D06AX09"]', '[]');
