INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25399, 'Prostec 5 mg 14 f.c.tab.', 'بروستك 5 مجم 14 قرص', '47', NULL, '5 alpha-reductase inhibitor', 'Finasteride', 'Sigma', 'Tab', 'أقراص', '5 mg', '2', 'علاج امراض الجهاز البولي والتناسلي -- تقليل الالتهابات', 'about finasteride 5 alpha-reductase inhibitor synthetic anti-androgen. mechanism of action of finasteride this synthetic 4-azasteroid derivative competitively inhibits steroid 5alpha reductase which is an enzyme responsible for active androgen dehydrotest', '6221051010129', NULL, 1408, '2022-12-08', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Finasteride is a synthetic 4-azasteroid compound [L10565] and specific inhibitor of steroid Type II 5α-reductase, which is an intracellular enzyme that converts the androgen testosterone into 5α-dihydrotestosterone (DHT). It works in a similar fashion as [dutasteride], which is another 5-alpha-reductase inhibitor, by exerting antiandrogenic effects. Finasteride is an orally active drug that was first approved by the FDA in 1992 for the treatment of benign prostatic hyperplasia to improve symptoms and reduce the risk for acute urinary retention or the need for surgical procedures.[L6244,L10565] In 1998, it was approved by the FDA to treat male pattern hair loss.[L6244] Finasteride is commonly marketed under the brand names Propecia and Proscar to be used aloneo or in combination with [doxazosin], an alpha-blocker.

Both benign prostatic hyperplasia and androgenic alopecia are androgen-dependent disorders that are characterized by _in situ_ high levels of DHT.[A178159] In the treatment of benign prostate hyperplasia, alpha-blockers such as [tamsulosin] and [terazosin] are also used. Compared to alpha-blockers that focus on providing the rapid relief of symptoms, 5α-reductase inhibitors aim to target the underlying disease by blocking the effects of the primary androgen involved in benign prostate hyperplasia and androgenic alopecia, thus reducing the risk for secondary complications while providing symptom control.[A2132]', '["D11AX10", "G04CA51", "G04CB01"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25400, 'Prostetrol 10mg m.r. 30 tab.', 'بروستيترول 10مجم 30 اقراص', '65', '45', 'Antihypertensive.selective alpha 1 receptor blockers', 'Alfuzosin', 'Eva pharma', 'Tab', 'أقراص', '10mg', '3', 'علاج مرض ارتفاع ضغط الدم', 'description alfuzosin (inn provided as the hydrochloride salt) is an alpha-adrenergic blocker used to treat benign prostatic hyperplasia indication : for the reduction of urinary obstruction and relief of associated manifestations (eg. sensation of incomp', '6223002453801', NULL, 2165, '2024-03-23', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Benign prostatic hyperplasia (BPH) refers to a benign growth or hyperplasia of the prostate and leads to lower urinary tract symptoms in men, such as urgency, frequency and changes to urine flow. The prevalence of BPH is as high as 50%-60% for males in their 60''s, and this prevalence increases to 80%-90% of those over 70.[A228483] Alfuzosin is an alpha-1 adrenergic blocker used in the symptomatic treatment of BPH that works by relaxing the muscles in the prostate and bladder neck.[L31593] It was initially approved by the FDA in 2003 and is marketed by several pharmaceutical companies.[L9251]', '["G04CA01", "G04CA51"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25401, 'Prostigmin (roche) vials 12.5 mg/5 ml', 'بروستيجمين امبول 12.5مجم/5مل', '11', NULL, 'Para-sympathomimetic', 'Neostigmine', 'Eipico > f.hoffman la roche', 'Vial', 'فيال', '12.5 mg', '1', NULL, 'about neostigmine para-sympathomimetic a reversible anticholinesterase in myasthenia gravis treatment an adjunct in general anaesthesia curare antagonist. mechanism of action of neostigmine it inhibits the destruction of acetylcholine by cholinesterase (b', '6221032311726', NULL, 1176, '2022-12-08', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'A cholinesterase inhibitor used in the treatment of myasthenia gravis and to reverse the effects of muscle relaxants such as gallamine and tubocurarine. Neostigmine, unlike physostigmine, does not cross the blood-brain barrier.', '["N07AA01", "S01EB06", "N07AA51"]', '[]');
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (25402, 'prostplasia 0.5mg 30 soft gelatin caps.', 'بروستبلازيا 0.5مجم 30 كبسولة', '138', '102', '6 alpha-reductase inhibitor', 'dutasteride', 'arab caps > suecal pharmaceuticals', 'capsule', 'كبسولة', '0.5mg', '3', NULL, 'about dutasteride a 5-alpha-reductase inhibitor in benign prostatic hyperplasia(bph) male baldness. mechanism of action of dutasteride this is a selective inhibitor of both type 1 and type 2 isoforms of steroid 5alpha-reductase (an intracellular enzyme th', NULL, NULL, 1578, '2025-12-27', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Dutasteride is an oral synthetic 4-azasteroid commonly marketed under the trade name Avodart. It is a novel dual 5α-reductase inhibitor that works by blocking both isoforms of 5α-reductase enzymes in a potent, selective, and irreversible manner.[A1909] Type I and II 5α-reductase enzymes convert testosterone into dihydrotestosterone (DHT), a primary hormonal mediator that plays a role in the development and enlargement of the prostate gland. Dutasteride was approved by the FDA in 2001 for the treatment of symptomatic benign prostatic hyperplasia (BPH) in men as monotherapy or in combination with the α-adrenergic antagonist [tamsulosin] to enhance the therapeutic response. Its clinical efficacy against benign prostate hyperplasia in male patients is comparable to that of [finasteride], a specific type II 5α-reductase inhibitor. However, unlike finasteride, dutasteride is not yet indicated for the treatment of androgenic alopecia although it was demonstrated to be effective in several randomized, double-blind, placebo-controlled trials in androgenetic alopecia.[A178333,A178336]', '["G04CB02", "G04CA52"]', '[]');
