INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7574, 'Lovir 400mg 10 disp. tab.', 'لوفير 400مجم 10 اقراص', '76', '50', 'Antiviral.nucleoside', 'Acyclovir', 'Global napi pharmaceuticals > ranbaxy', 'Tab', 'أقراص', '400mg', '2', 'مضاد للفيروسات', NULL, '6224009052189', NULL, 5316, '2024-07-07', 'An acyclovir topical cream is indicated to treat recurrent herpes labialis in immunocompetent patients 12 years and older.[L7303] Acyclovir oral tablets, capsules, and suspensions are indicated to treat herpes zoster, genital herpes, and chickenpox.[L7315] An acyclovir topical ointment is indicated to treat initial genital herpes and limited non-life-threatening mucocutaneous herpes simplex in immunocompromised patients.[L7318] An acyclovir cream with hydrocortisone is indicated to treat recurrent herpes labialis, and shortening lesion healing time in patients 6 years and older.[L7321] An acyclovir buccal tablet is indicated for the treatment of recurrent herpes labialis.[L7324] An acyclovir ophthalmic ointment is indicated to treat acute herpetic keratitis.[L7327]', 'Acyclovir is becomes acyclovir monophosphate due to the action of viral thymidine kinase.[A180757] Acyclovir monophosphate is converted to the diphosphate form by guanylate kinase.[A903] Acyclovir diphosphate is converted to acyclovir triphosphate by nucleoside diphosphate kinase, pyruvate kinase, creatine kinase, phosphoglycerate kinase, succinyl-CoA synthetase, phosphoenolpyruvate carboxykinase and adenylosuccinate synthetase.[A903,A180781] Acyclovir triphosphate has higher affinity for viral DNA polymerase than cellular DNA polymerase and incorporates into the DNA where the missing 2'' and 3'' carbons causes DNA chain termination.[A180757] In other cases acyclovir triphosphate competes so strongly for viral DNA polymerase that other bases cannot associate with the enzyme, inactivating it.[A180757]', 'Acyclovir is a deoxynucleoside analog that inhibits the action of viral DNA polymerase and DNA replication of different herpesvirus.[L7303,L7315,L7318,L7321,L7324,L7327] Acyclovir has a wide therapeutic window as overdose is rare in otherwise healthy patients.[L7315]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24873, 'Phenylephrine 10 % eye drops', 'فينيل ايفرين 10% نقط للعين', '5', NULL, 'Mydriatic', 'Phenylephrine', 'Misr', 'Drops', 'نقط', '10 %', '1', NULL, 'about phenylephrine an a1-adrenergic receptor agonist phenethylamine derivative decongestant antiglaucoma mydriatic. mechanism of action of phenylephrine the drug exerts it`s pharmacological action by acting as a selective alpha-1 adrenergic agonist. it c', NULL, NULL, 997, '2022-07-08', 'Phenylephrine is available in various drug formulations, which have different indications. Phenylephrine injections are indicated to treat hypotension caused by shock or anesthesia.[L9416, L9410] The ophthalmic formulation is indicated to induce mydriasis [L9413, L46332] and conjunctival vasoconstriction.[A187370] The intranasal formulation is used to treat congestion, and a topical formulation is used to treat hemorrhoids.[A187370] Off-label uses include priapism and induction of local vasoconstriction.[A187370]', 'Phenylephrine is an alpha-1 adrenergic agonist that mediates vasoconstriction[A187370] and mydriasis[L9413] depending on the route and location of administration. Systemic exposure to phenylephrine also leads to agonism of alpha-1 adrenergic receptors, raising systolic and diastolic pressure as well as peripheral vascular resistance.[L9416,L9410] Increased blood pressure stimulates the vagus nerve, causing reflex bradycardia.[L9416,L9410]', 'Phenylephrine is an alpha-1 adrenergic agonist that raises blood pressure,[L9416,L9410] dilates the pupils,[L9413] and causes local vasoconstriction.[A187370] Ophthalmic formulations of phenylephrine act for 3-8 hours[L9413] while intravenous solutions have an effective half life of 5 minutes and an elimination half life of 2.5 hours.[A187379,L9416] Patients taking ophthalmic formulations of phenylephrine should be counselled about the risk of arrhythmia, hypertension, and rebound miosis.[L9413] Patients taking an intravenous formulation should be counselled regarding the risk of bradycardia, allergic reactions, extravasation causing necrosis or tissue sloughing, and the concomitant use of oxytocic drugs.[L9416,L9410]', 'DrugBank', 0, 0, 0);
