INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (27402, 'Spirex 3 m.i.u. 10 tab', 'سبيركس 3 مليون وحدة دولية 10 اقراص', '108', '42', 'Antibiotic.macrolide', 'Spiramycin', 'Mup', 'Tab', 'أقراص', NULL, '1', 'مضاد حيوي', NULL, '6221508016919', NULL, 2626, '2025-05-06', 'Macrolide antibiotic for treatment of various infections.', 'The mechanism of action of macrolides has been a matter of controversy for some time. Spiramycin, a 16-membered macrolide, inhibits translocation by binding to bacterial 50S ribosomal subunits with an apparent 1 : 1 stoichiometry. This antibiotic is a potent inhibitor of the binding to the ribosome of both donor and acceptor substrates. The primary mechanism of action is done by stimulation of dissociation of peptidyl-tRNA from ribosomes during translocation.I ', 'The absolute bioavailability of oral spiramycin is generally within the range of 30 to 40%. After a 1 g oral dose, the maximum serum drug concentration was found to be within the range 0.4 to 1.4 mg/L.', 'DrugBank', 0, 0, 0);
