INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15824, 'Carboplatine mylan 150mg/15ml vial', 'كاربوبلاتين مايلان 150مجم/15 ملي فيال', '654', '457', 'Antineoplastic', 'Carboplatin', 'Mylan s.a.s-france > one pharma medics', 'Vial', 'فيال', '150mg', '1', NULL, NULL, NULL, NULL, 1152, '2025-02-04', 'Carboplatin is indicated in combination with an established combination of chemotherapeutic agents for the initial treatment of advanced ovarian carcinoma.[L32253] Carboplatin is also indicated for the palliative treatment of ovarian carcinoma, recurrent after prior chemotherapy.[L32253]', 'Carboplatin predominantly acts by attaching alkyl groups to the nucleotides, leading to the formation of monoadducts, and DNA fragmenting when repair enzymes attempt to correct the error.[A230158,L32253] 2% of carboplatin''s activity comes from DNA cross-linking from a base on one strand to a base on another, preventing DNA strands from separating for synthesis or transcription.[A230158,A1472] Finally, carboplatin can induce a number of different mutations.[A230158]', 'Carboplatin is an organoplatinum antineoplastic alkylating agent used in the treatment of advanced ovarian carcinoma.[L32253] Carboplatin has a long duration of action as it is given every 4 weeks, and a narrow therapeutic index.[L32253] Patients should be counselled regarding bone marrow suppression and anemia.[L32253]', 'DrugBank', 0, 0, 0);
