INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28075, 'Terventin 20mg 10 f.c.tab.', 'ترفنتين 20مجم 10 اقراص', '18', NULL, 'Bronchodilator.long acting beta 2 agonist', 'Bambuterol', 'Egyphar', 'Tab', 'أقراص', '20mg', '1', NULL, 'about bambuterol long acting beta 2-adrenoceptor agonist anti asthmatic-bronchodilators. mechanism of action of bambuterol this prodrug of the 2-agonist terbutaline undergoes oxidation and hydrolysis in the body and converted into terbutaline. this terbut', '6224001026546', NULL, 735, '2022-12-13', 0, 'For the prevention and reversal of bronchospasm in patients 12 years of age and older with asthma and reversible bronchospasm associated with bronchitis and emphysema.', 'The pharmacologic effects of bambuterol are at least in part attributable to stimulation through beta-adrenergic receptors (beta 2 receptors) of intracellular adenyl cyclase, the enzyme that catalyzes the conversion of adenosine triphosphate (ATP) to cyclic AMP. Increased cyclic AMP levels are associated with relaxation of bronchial smooth muscle and inhibition of release of mediators of immediate hypersensitivity from cells, especially from mast cells.', 'Bambuterol is a long acting beta2-adrenoceptor agonist used in the treatment of asthma. It is a prodrug of terbutaline. Bambuterol causes smooth muscle relaxation, resulting in dilation of bronchial passages.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28077, 'Testonon 250mg/ml i.m 1 oily amp.', 'تيستونون 250مجم/مل عضل 1 امبول', '20', NULL, 'Androgen', 'Testosterone', 'El nile.', 'Amp', 'أمبول', '250mg', '1', NULL, 'testosterone propionate ... 30 mg testosterone phenyl propionate ... 60 mg testosterone isocarproate ... 60 mg testosterone decanoate ... 100 mg a steroid hormone androgen. mechanism of action of testosterone testosterone and it`s reduced form; dihydrotes', '6221077127719', NULL, 1661, '2022-12-13', 0, 'Testosterone is indicated to treat primary hypogonadism and hypogonadotropic hypogonadism.[L8983,L8935,L8938,L8986,L8989,L8992,L8995]', 'The androgen receptor exists in the cytoplasm bound to the heat shock proteins HSP90, HSP70, and other chaperones.[A187114] After binding to an androgen, the androgen receptor dissociates from HSP90 and undergoes a conformational change to slow the rate of dissociation from the androgen receptor.[A187114] The androgen-receptor complex is transported into the nucleus where it binds to DNA and recruits other transcriptional regulators to form a pre-initiation complex and eventually induce expression of specific genes.[A187114]



Testosterone and its active metabolite dihydrotestosterone (DHT) antagonize the androgen receptor to develop masculine sex organs including the prostate, seminal vesicles, penis, and scrotum.[A187111,L8983,L8935,L8938,L8986,L8989,L8992,L8995]



Antagonism of the androgen receptor is also responsible for the development of secondary sexual characteristics including facial and body hair, enlargement of the larynx, thickening of the vocal cords, and changes in muscle and fat distribution.[A187111,L8983,L8935,L8938,L8986,L8989,L8992,L8995]', 'Testosterone antagonizes the androgen receptor to induce gene expression that causes the growth and development of masculine sex organs and secondary sexual characteristics.[A187114,L8983,L8935,L8938,L8986,L8989,L8992,L8995] The duration of action of testosterone is variable from patient to patient with a half life of 10-100 minutes.[L8983,L8935,L8938,L8986,L8989,L8992,L8995] The therapeutic index is wide considering the normal testosterone levels in an adult man range from 300-1000ng/dL.[L8983,L8938,L8986,L8989,L8992,L8995] Counsel patients regarding the risk of secondary exposure of testosterone topical products to children.[L8983,L8938,L8986,L8989,L8992]', 'DrugBank', 0, 0, 0);
