INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29113, 'Vent eye contour cream 15 gm', 'فنت كريم لمحيط العين والهالات السوداء 15 جرام', '99', NULL, 'Eye contour', 'Vitamin c+licorice extract+chamomile+panthenol+dimethicone+centella+caffeine+titanium dioxide', 'Hi-care > farabi', 'Cream', 'كريم', '15 gm', '1', 'تفتيح الهالات السوداء.', 'balanced formula for safe eye contour and sensitive skin care. ** application: apply cream & gently massage with pressing around the eye twice daily.', '6224007935095', NULL, 4325, '2023-04-26', 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29114, 'Ventair 20mg 20 f.c. tab.', 'فينتير 20مجم 20 اقراص', '50', NULL, 'Selective leukotriene d4 and e4 antagonist', 'Zafirlukast', 'Delta pharma', 'Tab', 'أقراص', '20mg', '2', NULL, 'indication for the prophylaxis and chronic treatment of asthma. mechanism of action zafirlukast is a selective and competitive receptor antagonist of leukotriene d4 and e4 (ltd4 and lte4) components of slow-reacting substance of anaphylaxis (srsa). cystei', NULL, NULL, 785, '2022-07-25', 0, 'For the prophylaxis and chronic treatment of asthma.', 'Zafirlukast is a selective and competitive receptor antagonist of leukotriene D4 and E4 (LTD<sub>4</sub> and LTE4), components of slow-reacting substance of anaphylaxis (SRSA). Cysteinyl leukotriene production and receptor occupation have been correlated with the pathophysiology of asthma, including airway edema, smooth muscle constriction, and altered cellular activity associated with the inflammatory process, which contribute to the signs and symptoms of asthma.', 'Zafirlukast is a synthetic, selective peptide leukotriene receptor antagonist (LTRA) indicated for the prophylaxis and chronic treatment of asthma. Patients with asthma were found in one study to be 25-100 times more sensitive to the bronchoconstricting activity of inhaled LTD<sub>4</sub> than nonasthmatic subjects. <i>In vitro</i> studies demonstrated that zafirlukast antagonized the contractile activity of three leukotrienes (LTC<sub>4</sub>, LTD<sub>4</sub> and LTE<sub>4</sub>) in conducting airway smooth muscle from laboratory animals and humans. Zafirlukast prevented intradermal LTD<sub>4</sub>-induced increases in cutaneous vascular permeability and inhibited inhaled LTD<sub>4</sub>-induced influx of eosinophils into animal lungs.', 'DrugBank', 0, 0, 0);
