INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23660, 'No more acne cleanser 250 ml', 'نو مور اكني غسول 250 مل', '165', '145', 'Anti-acne', 'Vitamins(a+c+e)+myristic acid+lauric acid+palmitic acid+salicylic acid+panthenol+tea tree oil', 'Italian co. for cosemetics > hexa group', 'Cleanser', 'منظف', '250 ml', '1', 'علاج حب الشباب', 'for oily & acne prone skin. unique new formula gentle on skin. use: apply a small amount to a clean fingertip and gently rub into a damp face then wash with water.', NULL, NULL, 2530, '2025-01-22', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25787, 'Refresh liquigel 1% eye drops 15 ml', 'ريفريش ليكويجل 1% قطرة عين 15 مل', '40', NULL, 'Eye lubricant', 'Sodium carboxymethylcellulose', 'Allergan > soficopharm', 'Gel', 'جل', '1%', '1', NULL, 'about sodium carboxy methyl cellulose ophthalmic lubricant artificial tears mechanism of action of sodium carboxy methyl cellulose keep the eye moist help to protect the eye from injury and infection oxychloro complex is used as preservative and this ulti', NULL, NULL, 1825, '2022-07-24', 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26751, 'Selsun blue 118 ml itchy dry scalp shampoo (n/a)', 'سيلسن بلو 118مل شامبو', '35', NULL, 'Hair care', 'Selenium sulfide', 'Famar > ramco', 'Amp', 'أمبول', '118 ml', '1', NULL, 'anti-dandruff shampoo', NULL, NULL, 706, '2022-07-29', 'For treatment of tinea versicolor, tinea capitis, dandruff and seborrheic dermatitis of the scalp.', 'Topical selenium sulfide may act by an antimitotic action, resulting in a reduction in the turnover of epidermal cells. It also has local irritant, antibacterial, and mild antifungal activity, which may contribute to its effectiveness. An antimitotic mechanism of action is suggested by data showing that selenium sulfide decreases the rate of incorporation of radioactively labeled thymidine into the DNA of dermal epithelial cells. The following organisms are generally considered susceptible to selenium sulfide in vitro: <i>Malassezia furfur</i>, <i>Microsporum sp.</i> including <i>Microsporum audouinii</i> and <i>Microsporum canis</i>, <i>Pityrosporon sp.</i>, <i>Trichophyton sp.</i> including <i>Trichophyton schoenleinii</i> and <i>Trichophyton tonsurans</i>. Selenium sulfide has been shown to be sporicidal to <i>T. tonsurans</i>, the most common etiologic agent of tinea capitis. One in-vitro study demonstrated that 2.5% selenium sulfide was equivalent in sporicidal activity to both 1% and 2% zinc pyrithione.', 'Selenium sulfide is an antifungal agent often used in shampoos for the treatment of dandruff and seborrheic dermatitis. Selenium sulfide is highly active in inhibiting the growth of <i>P. ovale</i>. It is also a proven cytostatic agent, slowing the growth of both hyperproliferative and normal cells in dandruff and seborrheic dermatitis. A 0.6% micronized form of selenium sulfide is also safe and effective for dandruff.', 'DrugBank', 0, 0, 0);
