INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (22050, 'Losar 50mg 28 f.c. tab.', 'لوسار 50 مجم 28 قرص', '82', '58', 'Antihypertensive.angiotensin blocker', 'Losartan potassium', 'Unipharma co.', 'Tab', 'أقراص', '50mg', '4', NULL, 'about losartan potassium an angiotensin ii receptor antagonist antihypertensive. mechanism of action of losartan potassium it is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive i', '6224000730635', NULL, 1635, '2024-01-28', 'Losartan is indicated to treat hypertension in patients older than 6 years, reduce the risk of stroke in patients with hypertension and left ventricular hypertrophy (though this benefit may not extend to patients with African heritage), and to treat diabetic nephropathy with elevated serum creatinine and proteinuria in patients with type 2 diabetes and hypertension.[L7423] Losartan with hydrochlorothiazide is indicated to treat hypertension and to reduce the risk of stroke in patients with hypertension and left ventricular hypertrophy (though this benefit may not extend to patients with African heritage).[L7426,L45663]', 'Losartan reversibly and competitively prevents angiotensin II binding to the AT<sub>1</sub> receptor in tissues like vascular smooth muscle and the adrenal gland.[L7423,L7426] Losartan and its active metabolite bind the AT<sub>1</sub> receptor with 1000 times more affinity than they bind to the AT<sub>2</sub> receptor.[L7423,L7426] The active metabolite of losartan is 10-40 times more potent by weight than unmetabolized losartan as an inhibitor of AT<sub>1</sub> and is a non-competitive inhibitor.[L7423,L7426] Losartan''s prevention of angiotensin II binding causes vascular smooth muscle relaxation, lowering blood pressure.[L7423,L7426]



Angiotensin II would otherwise bind to the AT<sub>1</sub> receptor and induce vasoconstriction, raising blood pressure.[L7423,L7426]', 'Losartan is an angiotensin II receptor blocker used to treat hypertension, diabetic nephropathy, and to reduce the risk of stroke.[A1033,L7423,L7426] Losartan has a long duration of action as it is given once daily.[L7423,L7426] Patients taking losartan should be regularly monitored for hypotension, renal function, and potassium levels.[L7423,L7426]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (669, 'Ampicillin-misr 250mg/5ml susp. 60ml', 'امبيسللين 250مجم معلق 60 مل مصر', '17', NULL, 'Penicillins.monocomponent', 'Ampicillin', 'Misr', 'Susp', 'معلق', '250mg', '1', NULL, NULL, NULL, NULL, 1586, '2023-04-07', 'For treatment of infection (Respiratory, GI, UTI and meningitis) due to E. coli, P. mirabilis, enterococci, Shigella, S. typhosa and other Salmonella, nonpenicillinase-producing N. gononhoeae, H. influenzae, staphylococci, streptococci including streptoc', 'By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, Ampicillin inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins; it is possible that Ampicillin interferes with an autolysin inhibitor.', 'Ampicillin is a penicillin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms. The name "penicillin" can either refer to several variants of penicillin available, or to the group of antibiotics derived from the penicillins. Ampicillin has <i>in vitro</i> activity against gram-positive and gram-negative aerobic and anaerobic bacteria. The bactericidal activity of Ampicillin results from the inhibition of cell wall synthesis and is mediated through Ampicillin binding to penicillin binding proteins (PBPs). Ampicillin is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended spectrum beta-lactamases.', 'DrugBank', 0, 0, 0);
