INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (24249, 'Orfixitin 1 gm i.m./i.v. vial', 'اورفيكسيتين 1 جم عضل/وريد فيال', '30', NULL, 'Antibiotic.cephalosporin.second-generation', 'Cefoxitin', 'Badr pharma > biomed pharmaceuticals', 'Vial', 'فيال', '1 gm', '1', NULL, 'indication for the treatment of serious infections caused by susceptible strains microorganisms. pharmacodynamics cefoxitin is a cephamycin antibiotic often grouped with the second-generation cephalosporins. it is active against a broad range of gram-nega', NULL, NULL, 1461, '2022-07-06', 'For the treatment of serious infections caused by susceptible strains microorganisms.', 'The bactericidal action of cefoxitin results from inhibition of cell wall synthesis.', 'Cefoxitin is a cephamycin antibiotic often grouped with the second-generation cephalosporins. It is active against a broad range of gram-negative bacteria including anaerobes. The methoxy group in the 7a position provides cefoxitin with a high degree of stability in the presence of beta-lactamases, both penicillinases and cephalosporinases, of gram-negative bacteria.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21836, 'Linahayzin 2.5mg/5 ml syrup 120 ml', 'ليناهيزين 2.5 مجم / 5 مل شراب 120 مل', '15', NULL, 'Antihistamine.non-sedative.third-generation', 'Levocetirizine', 'Egpi > health line international', 'Syrup', 'شراب', '2.5mg', '1', NULL, 'about levocetirizine a third-generation non-sedative h1 antagonist antihistamine. mechanism of action of levocetirizine this active enantiomer of cetrizine is a potent nonsedative and selective h1 receptor antagonist. it antagonizes the allergic symptoms', '6225000084520', NULL, 830, '2022-07-14', 'Levocetirizine is indicated to treat symptoms of perennial allergic rhinitis and uncomplicated skin manifestations of chronic idiopathic urticaria.[L7694] It is also used over the counter for a variety of mild allergy symptoms.[A181748]', 'Levocetirizine selectively inhibits histamine H<sub>1</sub> receptors.[L7694] This action prevents histamine from activating this receptor and causing effects like smooth muscle contraction, increased permeability of vascular endothelium, histidine uptake in basophils, stimulation of cough receptors, and stimulation of flare responses in the nervous system.[A181790]', 'Levocetirizine is a second generation histamine H<sub>1</sub> antagonist used to treat various allergic symptoms.[A181748,A181790,L7694] It has a long duration of action as it is generally taken once daily, and a wide therapeutic window as animal studies show the maximal nonlethal dose is over 100x a normal dose.[L7694] Patients are cautioned to avoid tasks that require complete alertness, avoid alertness, and use caution in patients with factors predisposing urinary retention.[L7694]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (954, 'Arixtra 2.5mg/0.5ml 10 s.c. prefilled syringes', 'اريكسترا 2.5 مجم /0.5 مل 10 حقن معبأة', '1,382', '921', 'Anticoagulant.low mwt heparin synthetic pentasaccharide', 'Fondaparinux sodium', 'Glaxo smithkline > phoenix-egypt', 'Syringe', 'حقنة', '2.5mg', '10', 'مضاد للتجلط', NULL, NULL, NULL, 7899, '2025-03-21', 'Approved for: (1) prophylaxis of VTE for up to one month post surgery in patients undergoing orthopedic surgery of the lower limbs such as hip fracture, hip replacement and knee surgery; (2) prophylaxis of VTE patients undergoing abdominal surgery who are at high risk of thromboembolic complications (e.g. patients undergoing abdominal cancer surgery); (3) treatment of acute DVT and PE; (4) management of UA and NSTEMI for the prevention of death and subsequent myocardial infarction (MI); and (5) management of STEMI for the prevention of death and myocardial reinfarction in patients who are managed with thrombolytics or who are initially to receive no form of reperfusion therapy. Fondaparinux should not be used as the sole anticoagulant during percutaneous coronary intervention (PCI) due to an increased risk of guiding catheter thrombosis. ', 'The antithrombotic activity of fondaparinux is the result of ATIII-mediated selective inhibition of Factor Xa. By selectively binding to ATIII, Fondaparinux potentiates (about 300 times) the neutralization of Factor Xa by ATIII. Neutralization of Factor Xa interrupts the blood coagulation cascade and thus inhibits thrombin formation and thrombus development. It is thought that fondaparinux is unlikely to induce thrombocytopenia via a heparin-induced thrombocytopenia (HIT)-like mechanism given its chemical structure [A17488]. As a result, fondaparinux has been used as an alternative anticoagulant in heparin-induced thrombocytopenia (HIT) patients [A33876,A33877,A33878]. However, it is important to note that rare cases of HIT have been reported in patients treated with fondaparinux [A33879,A33880].', 'Fondaparinux binds specifically to the natural anticoagulant factor, ATIII. Binding to ATIII potentiates the neutralizing action of ATIII on Factor Xa 300-times. Neutralization of Factor Xa decreases the conversion of prothrombin to thrombin, which subsequently decreases the conversion of fibrinogen to fibrin (loose meshwork). The decrease in thrombin also decreases the activation of Factor XIII, which decreases the conversion of fibrin in its loose meshwork form to its stabilized meshwork form. Disruption of the coagulation cascade effectively decreases the formation of blood clots. Fondaparinux does not inactivate thrombin (activated Factor II). According to the manufacturer, fondaparinux has no known effect on platelet function. In studies comparing fondaparinux to enoxaparin, decreases in platelet levels were observed in similar numbers of patients from both groups (2-5%) [A871,A872]. At the recommended dose, Fondaparinux does not affect fibrinolytic activity or bleeding time. There is no antidote for fondaparinux. Monitoring of the anticoagulant activity of fondaparinux is not generally required. The anti-factor Xa assay may be used to monitor therapy in special populations such as those with renal impairment or who are pregnant. Complete blood count (CBC) and kidney function should be monitored during treatment. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29937, 'Zinc origin 0.2gm/100ml syrup 120 ml', 'زنك اوريجين 0.2جم/100مل شراب 120 مل', '31', '19', 'Zinc supplement', 'Zinc gluconate', 'Egpi > origin international pharma', 'Syrup', 'شراب', '0.2gm', '1', NULL, 'about zinc trance metal dietary mineral nutritional supplement treatment of zinc deficiency in wound healing. mechanism of action of zinc zinc acts as a cofactor for more than 70 different enzymes. zinc dependent enzymes are involved in the metabolism of', '6224000665067', NULL, 3537, '2024-06-02', 'Zinc gluconate is mainly indicated in conditions like zinc deficiency, and can also be administered in adjunctive therapy as an alternative drug of choice in diarrhea [L2088].', 'Although the mechanism of action is not completely known, zinc supplementation may be used to increase immunity against viruses or may interfere with the replication of certain viruses, such as the human papillomavirus (HPV) [L2080].



', 'Zinc is an important mineral found in almost every cell in the human body. It promotes the activity of about 100 enzymes. Zinc deficiency is often associated with an increased risk of infection. When they are used to treat the common cold, zinc supplements may interfere with rhinovirus cleavage or adhesion and may play a role in protecting plasma membranes from microbial toxins and complement [L2086].', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23175, 'Fatroxim 550 mg 30 f.c. tab', 'فاتروكسيم 550مجم 30 قرص', '516', '360', 'Travelers diarrhea', 'Rifaximin', 'Egpi > atm pharmaceutical industries', 'Tab', 'أقراص', '550 mg', '3', NULL, NULL, NULL, NULL, 815, '2024-07-15', 'Rifaximin has multiple indications by the FDA: for the treatment of patients (≥12 years of age) with traveller''s diarrhea caused by noninvasive strains of Escherichia coli; for the reduction of overt hepatic encephalopathy recurrence in patients ≥18 years of age; and in May 2015 it was approved for irritable bowel syndrome with diarrhea (IBS-D) treatment in adult men and women. ', 'Rifaximin acts by inhibiting RNA synthesis in susceptible bacteria by binding to the beta-subunit of bacterial deoxyribonucleic acid (DNA)-dependent ribonucleic acid (RNA) polymerase enzyme. This binding blocks translocation, which stops transcription.', 'Rifaximin is a structural analog of rifampin and a non-systemic, gastrointestinal site-specific antibiotic. This non-systemic property of the drug is due to the addition of a pyridoimidazole ring, which renders it non-absorbable. Rifaximin acts by inhibiting bacterial ribonucleic acid (RNA) synthesis and contributes to restore intestinal microflora imbalance. Other studies have also shown rifaximin to be an pregnane X receptor (PXR) activator. As PXR is responsible for inhibiting the proinflammatory transcription factor NF-kappa B (NF-κB) and is inhibited in inflammatory bowel disease (IBD), rifaximin was proven to be effective for the treatment of IBS-D.', 'DrugBank', 0, 0, 0);
