INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20697, 'Ice ramp massage gel 75 gm', 'ايس رامب مساج جل 75جم', '35', NULL, 'Massage', 'Glucosamine+camphor oil+menthol oil+eucalyptus oil+peppermint oil+panthenol', 'Healthy group > mepco pharma', 'Amp', 'أمبول', '75 gm', '1', NULL, NULL, NULL, NULL, 694, '2022-07-08', NULL, NULL, NULL, NULL, 0, 0, 0);
