INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29592, 'Vorifend 50mg 10 f.c. tabs.', 'فوريفيند 50مجم 10 قرص', '240', NULL, 'Antifungals.triazoles', 'Voriconazole', 'Chemipharm', 'Tab', 'أقراص', '50mg', '1', NULL, NULL, NULL, NULL, 1065, '2022-07-08', 'For the treatment of esophageal candidiasis, cadidemia, invasive pulmonary aspergillosis, and serious fungal infections caused by <i>Scedosporium apiospermum</i> and <i>Fusarium</i> spp.[L9821, L15571] ', 'Voriconazole is used to treat fungal infections caused by a variety of organisms but including _Aspergillus spp._ and _Candida spp_. Voriconazole is a triazole antifungal exhibiting fungistatic activity against fungal pathogens.[A236050, A236045, L9821] Like other triazoles, voriconazole binds to 14-alpha sterol demethylase, also known as CYP51, and inhibits the demethylation of lanosterol as part of the ergosterol synthesis pathway in yeast and other fungi. The lack of sufficient ergosterol disrupts fungal cell membrane function and limits fungal cell growth. With fungal growth limited, the host''s immune system is able to clear the invading organism.', 'Voriconazole is a fungistatic triazole antifungal used to treat infections by inhibiting fungal growth.[L9821] It is known to cause hepatotoxic and photosensitivity reactions in some patients.', 'DrugBank', 0, 0, 0);
