INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (32943, 'Heam smart 120 ml syrup', 'هيم سمارت 120مل شراب', '100', '70', 'Liposomal iron', 'Liposomal iron + vit c + folic acid + vit b', 'Smartec', 'Syrup', 'شراب', '120 ml', '1', 'مكمل غذئى لعلاج حالات الانيميا ودعم الجهاز العصبى والمناعى وامداد الجسم بالطاقة', NULL, NULL, NULL, 3755, '2025-08-05', 'Investigated for use/treatment in neuropathy (diabetic) and peripheral vascular disease.', 'The encapsulated formulation (Liprostin) has been shown to improve the therapeutic index of PGE-1, positively impacting many areas of treatment, such as heart attacks, occlusive disease, ischemic ulcers, critical limb salvage, claudicants, and arthritis.', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15449, 'Bosentadin 125 mg 10 f.c. tabs.', 'بوسنتادين 125 مجم 10 أقراص', '804', '699', 'Endothelian receptor antagonist', 'Bosentan', 'Eva pharma > biothecary', 'Tab', 'أقراص', '125 mg', '1', NULL, 'pharmacology endothelian receptor antagonist that blocks endothelin receptors on endothelium and vascular smooth muscle (stimulation of these receptors is associated with vasoconstriction). bosentan blocks both eta and etb receptors with a slightly higher', NULL, NULL, 1375, '2023-09-10', 'Used in the treatment of pulmonary arterial hypertension (PAH), to improve exercise ability and to decrease the rate of clinical worsening (in patients with WHO Class III or IV symptoms).', 'Endothelin-1 (ET-1) is a neurohormone, the effects of which are mediated by binding to ET<sub>A</sub> and ET<sub>B</sub> receptors in the endothelium and vascular smooth muscle. It displays a slightly higher affinity towards ET<sub>A</sub> receptors than ET<sub>B</sub> receptors. ET-1 concentrations are elevated in plasma and lung tissue of patients with pulmonary arterial hypertension, suggesting a pathogenic role for ET-1 in this disease. Bosentan is a specific and competitive antagonist at endothelin receptor types ET<sub>A</sub> and ET<sub>B</sub>.', 'Bosentan belongs to a class of drugs known as endothelin receptor antagonists (ERAs). Patients with PAH have elevated levels of endothelin, a potent blood vessel constrictor, in their plasma and lung tissue. Bosentan blocks the binding of endothelin to its receptors, thereby negating endothelin''s deleterious effects.', 'DrugBank', 0, 0, 0);
