INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28092, 'Teveten 600mg 14 f.c.tab.', 'تيفيتين 600مجم 14 اقراص', '67', NULL, 'Antihypertensive.angiotensin blocker', 'Eprosartan', 'Solvay pharmaceuticals > multipharma', 'Tab', 'أقراص', '600mg', '2', NULL, 'mechanism of action eprosartan blocks the vasoconstrictor and aldosterone-secreting effects of angiotensin ii by selectively blocking the binding of angiotensin ii to the at1 receptor found in many tissues (e.g. vascular smooth muscle adrenal gland). ther', NULL, NULL, 754, '2022-07-11', 'For the management of hypertension alone or in combination with other classes of antihypertensive agents. Also used as a first-line agent in the treatment of diabetic nephropathy, as well as a second-line agent in the treatment of congestive heart failure (only in those intolerant of ACE inhibitors).', 'Eprosartan blocks the vasoconstrictor and aldosterone-secreting effects of angiotensin II by selectively blocking the binding of angiotensin II to the AT<sub>1</sub> receptor found in many tissues (e.g., vascular smooth muscle, adrenal gland). There is also an AT<sub>2</sub> receptor found in many tissues but it is not known to be associated with cardiovascular homeostasis. Eprosartan does not exhibit any partial agonist activity at the AT<sub>1</sub> receptor. Its affinity for the AT<sub>1</sub> receptor is 1,000 times greater than for the AT<sub>2</sub> receptor. In vitro binding studies indicate that eprosartan is a reversible, competitive inhibitor of the AT<sub>1</sub> receptor. Eprosartan has also been shown to bind to AT<sub>1</sub> receptors both presynaptically and synaptically. Its action on presynaptic AT<sub>1</sub> receptors results in the inhibition of sympathetically stimulated noradrenaline release. Unlike ACE inhibitors, eprosartan and other ARBs do not interfere with response to bradykinins and substance P, which allows for the absence of adverse effects that are present in ACE inhibitors (eg. dry cough).', 'Angiotensin II, the principal pressor agent of the renin-angiotensin system, is formed from angiotensin I in a reaction catalyzed by angiotensin-converting enzyme [kininase II]. It is responsible for effects such as vasoconstriction, stimulation of synthesis and release of aldosterone, cardiac stimulation, and renal reabsorption of sodium. Eprosartan selectively blocks the binding of angiotensin II to the AT<sub>1</sub> receptor, which in turn leads to multiple effects including vasodilation, a reduction in the secretion of vasopressin, and reduction in the production and secretion of aldosterone. The resulting effect is a decrease in blood pressure.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14736, 'Averolosin 2 gm gr. 7 sachets', 'افيرولوزين 2 جم 7 اكياس', '43', NULL, 'Osteoporosis.antiresorptives', 'Strontium-89 chloride', 'Averroes pharma-egypt', 'Sachet', 'أكياس', '2 gm', '1', NULL, 'about strontium bone metabolism modulator antiosteoporotic agent mechanism of action of strontium strontium is an antiosteoporotic agent which both increases bone formation and reduces bone resorption. strontium stimulates the calcium sensing receptors an', NULL, NULL, 707, '2022-07-13', NULL, NULL, NULL, NULL, 0, 0, 0);
