INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (5828, 'Haema-caps 14 caps', 'هيما-كابس 14 كبسولة', '18', NULL, 'Anemia.iron supplement', 'Ferrous fumarate+vitamins(b1+b2+b6+b12+c+d+e)+folic acid+calcium+copper+linoleic acid+linolenic acid+manganese', 'Amoun', 'Cap', 'كبسولة', NULL, '2', 'مكمل غذائي بالحديد لعلاج الانيميا--لعلاج حالات الانيميا الشديدة ونقص الحديد --وتساقط الشعر الناتج عن الانيميا --وحالات الضعف العام والخمول والاجهاد', NULL, '6221025008305', NULL, 2380, '2022-12-05', 'Ferrous sulfate is used for the prevention and treatment of iron deficiency anemia in adults and children.[A190804,L2240,L11800]

', 'Iron is required to maintain optimal health, particularly for helping to form red blood cells (RBC) that carry oxygen around the body. A deficiency in iron indicates that the body cannot produce enough normal red blood cells.[A32514,L11800] Iron deficiency anemia occurs when body stores of iron decrease to very low levels, and the stored iron is insufficient to support normal red blood cell (RBC) production. Insufficient dietary iron, impaired iron absorption, bleeding, pregnancy, or loss of iron through the urine can lead to iron deficiency.[A32514,L11794]  Symptoms of iron deficiency anemia include fatigue, breathlessness, palpitations, dizziness, and headache.



Taking iron in supplement form, such as ferrous sulfate, allows for more rapid increases in iron levels when dietary supply and stores are not sufficient.[L2175] Iron is transported by the divalent metal transporter 1 (DMT1) across the endolysosomal membrane to enter the macrophage. It can then can be incorporated into ferritin and be stored in the macrophage or carried of the macrophage by ferroportin. This exported iron is  oxidized by the enzyme to ceruloplasmin to Fe3+, followed by sequestration by transferrin for transport in the serum to various sites, including the bone marrow for hemoglobin synthesis or into the liver.[A32524] Iron combines with porphyrin and globin chains to form hemoglobin, which is critical for oxygen delivery from the lungs to other tissues.[L2263]', 'Ferrous sulfate replenishes iron, an essential component in hemoglobin, myoglobin, and various enzymes. It replaces the iron that is usually found in hemoglobin and myoglobin. Iron participates in oxygen transport and storage, electron transport and energy metabolism, antioxidant and beneficial pro-oxidant functions, oxygen sensing, tissue proliferation and growth, as well as DNA replication and repair.[A32524,A32514]



', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16455, 'Claudol 50 mg 30 tab.', 'كلاويدول 50مجم 30 قرص', '47', NULL, 'Antiplatelet.phosphodiesterase 3 inhibitor', 'Cilostazol', 'Delta pharma > activa therapeutics', 'Tab', 'أقراص', '50 mg', '3', NULL, 'description cilostazol is a medication used in the alleviation of the symptom of intermittent claudication in individuals with peripheral vascular disease. it is manufactured by otsuka pharmaceutical co. under the trade name pletal. although drugs similar', NULL, NULL, 853, '2022-07-08', 'Indicated for the alleviation of symptoms of intermittent claudication (pain in the legs that occurs with walking and disappears with rest).', 'Cilostazol and several of its metabolites are cyclic AMP (cAMP) phosphodiesterase III inhibitors (PDE III inhibitors), inhibiting phosphodiesterase activity and suppressing cAMP degradation with a resultant increase in cAMP in platelets and blood vessels, leading to inhibition of platelet aggregation and vasodilation.', 'Cilostazol reduces the symptoms of intermittent claudication, as indicated by an increased walking distance. Intermittent claudication is pain in the legs that occurs with walking and disappears with rest. The pain occurs due to reduced blood flow to the legs.', 'DrugBank', 0, 0, 0);
