INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (625, 'Amovit-c 1 gm 12 eff. tab.', 'اموفيت سي 12 قرص فوار', '8', NULL, 'Vitamin c', 'Vitamin c', 'Amoun', 'Tab', 'أقراص', '1 gm', '1', NULL, 'about vitamin c water soluble vitamin antioxidant. mechanism of action of vitamin c vitamin c exerts it`s action by influencing the biologic oxidations and reductions used in cellular respirations. it directly stimulates collagen synthesis and maintains i', '6221025015709', NULL, 1322, '2022-12-01', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18821, 'Coxorizet 90 mg 30 tabs', 'كوكسوريزيت 90 مجم 30 قرص', '249', '169.5', 'Nsaid.selective cox-2 inhibitors', 'Etoricoxib', 'Zeta pharma', 'Tab', 'أقراص', '90 mg', '3', NULL, NULL, '6225000483774', NULL, 3490, '2025-05-08', 'For the treatment of rheumatoid arthritis, osteoarthritis, ankylosing spondylitis, chronic low back pain, acute pain and gout.', 'Like any other COX-2 selective inhibitor Etoricoxib selectively inhibits isoform 2 of cyclo-oxigenase enzyme (COX-2), preventing production of prostaglandins (PGs) from arachidonic acid.', 'Etoricoxib is a COX-2 selective inhibitor (approximately 106 times more selective for COX-2 inhibition over COX-1).', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15587, 'Butoconil 2% vaginal cream 15 gm', 'بوتوكونيل 2% كريم مهبلى 15 جم', '36', NULL, 'Antifungal', 'Butoconazole', 'El nile.', 'Cream', 'كريم', '2%', '1', NULL, NULL, NULL, NULL, 805, '2022-08-05', 'For the local treatment of vulvovaginal candidiasis (infections caused by Candida)', 'The exact mechanism of the antifungal action of butoconazole is unknown, however, it is presumed to function as other imidazole derivatives via inhibition of steroid synthesis. Imidazoles generally inhibit the conversion of lanosterol to ergosterol via the inhibition of the enzyme cytochrome P450 14&alpha;-demethylase, resulting in a change in fungal cell membrane lipid composition. This structural change alters cell permeability and, ultimately, results in the osmotic disruption or growth inhibition of the fungal cell.', 'Butoconazole is an imidazole derivative that has fungicidal activity <i>in vitro</i> against <i>Candida</i> spp. and has been demonstrated to be clinically effective against vaginal infections due to <i>Candida albicans</i>. <i>Candida albicans</i> has been identified as the predominant species responsible for vulvovaginal candidasis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (6219, 'Human albumin 20 % behring (low salt) (hospitals only)', 'البيومين بشري 20% فيال 50 مل برينج منخفض الملح مستشفيات فقط', '682', NULL, 'Hypoalbuminemia', 'Human albumin', 'Csl behering-gmbh > 2s pharma group', 'Unknown', 'غير محدد', '20 %', '1', 'علاج انخفاض البومين الدم', NULL, NULL, NULL, 4314, '2023-02-19', 'Human immunoglobulin G is indicated for the following conditions:



### Primary Immunodeficiency

