INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29604, 'Votrient 200 mg 30 f.c. tab.', 'فوترينت 200مجم 30 اقراص', '4,641', '2380', 'Tyrosine kinase inhibitor', 'Pazopanib', 'Glaxo smithkline > novartis scientific office', 'Tab', 'أقراص', '200 mg', '3', NULL, NULL, NULL, NULL, 1546, '2024-11-04', 0, 'Treatment of advanced renal cell cancer and advanced soft tissue sarcoma (in patients previously treated with chemotherapy) ', 'Pazopanib is a second-generation multitargeted tyrosine kinase inhibitor against vascular endothelial growth factor receptor-1, -2, and -3, platelet-derived growth factor receptor-alpha, platelet-derived growth factor receptor-beta, and c-kit. These receptor targets are part of the angiogenesis pathway that facilitates the formation of tumour blood vessel for tumour survival and growth. ', 'Pazopanib is a synthetic indazolylpyrimidine and reaches steady state concentrations of >15 μg/ml. This concentration is high enough to observe maximal inhibition of VEGFR2 phosphorylation and some anti-tumour activity (concentration required to inhibit receptors is 0.01 - 0.084 μmol/L). A reduction in tumour blood flow, increased tumour apoptosis, inhibition of tumour growth, reduction in tumour interstitial fluid pressure, and hypoxia in cancer cells can be observed in patients receiving treatment. ', 'DrugBank', 0, 0, 0);
