INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29644, 'Wellofung 2% topical cream 40 gm', 'ويلوفانج 2% كريم 40 جم', '48', '33.5', 'Antifungals.imidazoles', 'Ketoconazole', 'Egpi > health well', 'Cream', 'كريم', '2%', '1', NULL, 'about ketoconazole imidazole derivative a broad spectrum antifungal. mechanism of action of ketoconazole ketoconazole is fungicidal or fungistatic depending on concentrations. it inhibits the conversion of lanosterol to 14 demethyl lanosterol by inhibitin', '6224000911676', NULL, 4951, '2024-07-30', 0, 'Ketoconazole is used in the treatment or prevention of fungal infections including blastomycosis, candidiasis, coccidioidomycosis, histoplasmosis, chromomycosis, and paracoccidioidomycosis.[FDA Label] In Europe, it is also used in the treatment of endogenous Cushing''s syndrome.[L7736]', 'Ketoconazole interacts with 14-&alpha;-sterol demethylase, a cytochrome P-450 enzyme necessary for the conversion of lanosterol to ergosterol.[A181802,T116] This results in inhibition of ergosterol synthesis and increased fungal cellular permeability due to reduced amounts of ergosterol present in the fungal cell membrane. This metabolic inhibition also results in accumulation of 14α-methyl-3,6-diol, a toxic metabolite. The increase in membrane fluidity is also thought to produce impairment of membrane-bound enzyme systems as components become less closely packed.', 'Ketoconazole, similarly to other azole antifungals, is a fungistatic agent which causes growth arrest in fungal cells thereby preventing growth and spread of the fungus throughout the body.[T116]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29645, 'Weptonal 320mg 7 f.c. tabs.', 'ويبتونال 320مجم 7 اقراص', '82', NULL, 'Antibiotic.quinolone', 'Gemifloxacin', 'Pharmed healthcare', 'Tab', 'أقراص', '320mg', '1', NULL, 'about gemifloxacin fourth-generation fluoroquinolone(respiratory) antibacterial oral broad-spectrum fluoroquinolone antibiotic. mechanism of action of gemifloxacin gemifloxacin act by inhibiting the enzyme dna gyrase (topoisomerase 2) and topoisomerase 4.', NULL, NULL, 676, '2022-07-13', 0, 'For the treatment of bacterial infection caused by susceptible strains such as <i>S. pneumoniae</i>, <i>H. influenzae</i>, <i>H. parainfluenzae</i>, or <i>M. catarrhalis</i>, <i>S. pneumoniae</i> (including multi-drug resistant strains [MDRSP]), <i>M. pneumoniae</i>, <i>C. pneumoniae</i>, or <i>K. pneumoniae</i>.', 'The bactericidal action of gemifloxacin results from inhibition of the enzymes topoisomerase II (DNA gyrase) and topoisomerase IV, which are required for bacterial DNA replication, transcription, repair, and recombination.', 'Gemifloxacin is a quinolone/fluoroquinolone antibiotic. Gemifloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Gemifloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria.', 'DrugBank', 0, 0, 0);
