INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (29807, 'Yutopar 10mg 20 tab.', 'يوتوبار 10مجم 20 اقراص', '24', NULL, 'Uterine relaxant', 'Ritodrine', 'Pharco', 'Tab', 'أقراص', '10mg', '2', NULL, 'about ritodrine a beta-2 adrenergic agonist a tocolytic(uterine relaxant). mechanism of action of ritodrine ritodrine is 2 adrenergic agonist. it binds to ?2 adrenergic receptor on outer membrane of myometrial cell and activates adenylyl cyclase. this wil', '6221151000945', NULL, 649, '2022-12-15', 0, 'For the treatment and prophylaxis of premature labour', 'Ritodrine is beta-2 adrenergic agonist. It binds to beta-2 adrenergic receptors on outer membrane of myometrial cell, activates adenyl cyclase to increase the level of cAMP which decreases intracellular calcium and leads to a decrease of uterine contractions.', 'Beta-2 adrenergic receptors are located at sympathetic neuroeffector junctions of many organs, including uterus. Ritodrine is beta-2 adrenergic agonist. It stimulates beta-2 adrenergic receptor, increases cAMP level and decreases intracellular calcium concentration. The decrease of calcium concentration leads to a relaxation of uterine smooth muscle and, therefore, a decrease in premature uterine contractions. ', 'DrugBank', 0, 0, 0);
