INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26395, 'Rudac 150 mg 20 tab.', 'روداك 150مجم 20 قرص', '14', NULL, 'Nsaid.acetic acid derivatives', 'Sulindac', 'Sigma', 'Tab', 'أقراص', '150 mg', '2', NULL, 'about sulindac nsaid arylalkanoic acid derivative analgesic antiinflammatory. mechanism of action of sulindac sulindac produces its analgesics and anti-inflammatory actions by inhibiting prostaglandin synthesis. pharmacokinets of sulindac absorption: it i', '6221051010600', NULL, 789, '2023-01-14', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Sulindac is a nonsteroidal anti-inflammatory drug (NSAID) of the arylalkanoic acid class that is marketed by Merck under the brand name Clinoril. Like other NSAIDs, it may be used in the treatment of acute or chronic inflammatory conditions. Sulindac is a prodrug, derived from sulfinylindene, that is converted _in vivo_ to an active sulfide compound by liver enzymes. There is evidence from some studies that sulindac may be associated with fewer gastrointestinal side effects than other NSAIDs, except for the cyclooxygenase-2 (COX-2) inhibitor drug class. This may be due to the sulfide metabolite undergoing enterohepatic circulation thus maintaining constant blood levels of the compound without inducing gastrointestinal effects, where the drug is excreted in the bile and then reabsorbed from the intestines. While its full mechanism of action is not fully understood, sulindac is thought to primarily mediate its action by inhibiting prostaglandin synthesis by inhibiting COX-1 and COX-2.', '["M01AB02"]', '[]');
