INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (26832, 'Serigabtin 150mg 20 caps.', 'سيريجابتين 150مجم 20 كبسولة', '63', NULL, 'Anti-epileptic.gaba analogs', 'Pregabalin', 'Sedico > serio pharm-egypt', 'Cap', 'كبسولة', '150mg', '2', NULL, 'about pregabalin a gaba agonist anticonvulsant. mechanism of action of pregabalin pregabalin is a gaba neurotransmitter analog. it produces its action by modulating calcium channels and also reduces the release of neurotransmitters such as glutamate norep', '6225000092129', NULL, 715, '2022-12-08', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Pregabalin is structurally similar to gamma-aminobutyric acid (GABA) - an inhibitory neurotransmitter.[A173995] It may be used to manage neuropathic pain, postherpetic neuralgia, and fibromyalgia among other conditions.[A187190] Although as per the FDA Label the mechanism of action has not been definitively defined, there is evidence that pregabalin exerts its effects by binding to the α2δ subunit of voltage-dependent calcium channels.[A187190][L7066] Pregabalin is marketed by Pfizer under the trade name Lyrica and Lyrica Cr (extended release).[L1006][L7066] It may have dependence liability if misused but the risk appears to be highest in patients with current or past substance use disorders.[A31161]', '["N03AX16"]', '[]');
