INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (30553, 'Flowadjust 25mg 30 prolonged rel. tabs.', 'فلوادجاست 25مجم 30 قرص', '306', '234', 'Overactive bladder', 'Mirabegron', 'Amoun', 'Tab', 'أقراص', '25mg', '3', 'لتقليل عدد مرات التبول وعلاج حالات فرط نشاط المثانة', NULL, '6221025042057', NULL, 26006, '2024-09-29', 0, 'Mirabegron is indicated for the treatment of overactive bladder (OAB) - with symptoms of urge urinary incontinence, urgency, and urinary frequency - either alone or in combination with [solifenacin].[L32853] It is also indicated for the treatment of neurogenic detrusor overactivity (NDO) in pediatric patients 3 years of age and older and weighing 35kg or more.[L32853]', 'Mirabegron is a potent and selective agonist of beta-3 adrenergic receptors. The activation of beta-3 receptors relaxes detrusor smooth muscle during the storage phase of the urinary bladder fill-void cycle, which increases the bladder''s storage capacity thereby alleviating feelings of urgency and frequency.[L32853]', 'Mirabegron exerts its pharmacologic effects by forcing bladder smooth muscle to relax, thereby expanding its capacity and relieving urgency. Mirabegron does not appear to adversely affect the mean maximum flow rate or mean detrusor pressure at maximum flow rate in patients with lower urinary tract symptoms and bladder outlet obstruction (BOO), but should be used with in patients with BOO due to reports of significant urinary retention. Furthermore, mirabegron increases both blood pressure and heart rate in a dose-dependent manner and should therefore be used with caution in patients with severely uncontrolled hypertension or others for whom these increases may prove dangerous.[L32853]', 'DrugBank', 0, 0, 0);
