INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21153, 'Jedcoflacin 200 mg 20 f.c.tab.', 'جدكوفلاسين 200 مجم 20 قرص', '43', NULL, 'Antibiotic.quinolone', 'Ofloxacin', 'Jedco int. co. for pharmaceuticals', 'Tab', 'أقراص', '200 mg', '2', NULL, 'about ofloxacin a second generation fluoroquinolone(quinolones) antibiotic. mechanism of action of ofloxacin ofloxacin is a bactericidal drug. it act by inhibiting the enzyme dna gyrase (topoisomerase 2) and topoisomerase 4.dna gyrase helps in the formati', NULL, NULL, 958, '2022-07-13', 'For the treatment of infections (respiratory tract, kidney, skin, soft tissue, UTI), urethral and cervical gonorrhoea.', 'Ofloxacin acts on DNA gyrase and toposiomerase IV, enzymes which, like human topoisomerase, prevents the excessive supercoiling of DNA during replication or transcription. By inhibiting their function, the drug thereby inhibits normal cell division.', 'Ofloxacin is a quinolone/fluoroquinolone antibiotic. Ofloxacin is bactericidal and its mode of action depends on blocking of bacterial DNA replication by binding itself to an enzyme called DNA gyrase, which allows the untwisting required to replicate one DNA double helix into two. Notably the drug has 100 times higher affinity for bacterial DNA gyrase than for mammalian. Ofloxacin is a broad-spectrum antibiotic that is active against both Gram-positive and Gram-negative bacteria.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28347, 'Topzol-v 2% vaginal cream 20 gm', 'توبزول في 2 كريم 20جم', '13', NULL, 'Antifungals.imidazoles', 'Tioconazole', 'Hikma pharma', 'Cream', 'كريم', '2%', '1', NULL, 'indication for the local treatment of vulvovaginal candidiasis (moniliasis). mechanism of action tioconazole interacts with 14-a demethylase a cytochrome p-450 enzyme that converts lanosterol to ergosterol an essential component of the yeast membrane. in', NULL, NULL, 987, '2022-07-18', 'For the local treatment of vulvovaginal candidiasis (moniliasis).', 'Tioconazole interacts with 14-&alpha; demethylase, a cytochrome P-450 enzyme that converts lanosterol to ergosterol, an essential component of the yeast membrane. In this way, tioconazole inhibits ergosterol synthesis, resulting in increased cellular permeability. Tioconazole may also inhibit endogenous respiration, interact with membrane phospholipids, inhibit the transformation of yeasts to mycelial forms and the uptake of purine, impair triglyceride and/or phospholipid biosynthesis, and inhibit the movement of calcium and potassium ions across the cell membrane by blocking the ion transport pathway known as the Gardos channel.', 'Tioconazole is a broad-spectrum imidazole antifungal agent that inhibits the growth of human pathogenic yeasts. Tioconazole exhibits fungicidal activity in vitro against <i>Candida albicans</i>, other species of the genus Candida, and against <i>Torulopsis glabrata</i>. Tioconazole prevents the growth and function of some fungal organisms by interfering with the production of substances needed to preserve the cell membrane. In addition to its broad spectrum antifungal effects with tioconazole being effective against commonly occurring dermatophyte and yeast-like fungal species, tioconazole also has antibacterial effects on certain Gram-positive cocci bacteria.[L50166]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7553, 'Losartan 50mg 14 tab.', 'لوسارتان 50مجم 14 قرص', '18', NULL, 'Antihypertensive.angiotensin blocker', 'Losartan potassium', 'Pharopharma', 'Tab', 'أقراص', '50mg', '2', NULL, 'about losartan potassium an angiotensin ii receptor antagonist antihypertensive. mechanism of action of losartan potassium it is a competitive antagonist of angiotensin-2 at at-1 receptor. it decreases peripheral resistance and lowers bp in hypertensive i', '6221094026361', NULL, 2891, '2022-12-06', 'Losartan is indicated to treat hypertension in patients older than 6 years, reduce the risk of stroke in patients with hypertension and left ventricular hypertrophy (though this benefit may not extend to patients with African heritage), and to treat diabetic nephropathy with elevated serum creatinine and proteinuria in patients with type 2 diabetes and hypertension.[L7423] Losartan with hydrochlorothiazide is indicated to treat hypertension and to reduce the risk of stroke in patients with hypertension and left ventricular hypertrophy (though this benefit may not extend to patients with African heritage).[L7426,L45663]', 'Losartan reversibly and competitively prevents angiotensin II binding to the AT<sub>1</sub> receptor in tissues like vascular smooth muscle and the adrenal gland.[L7423,L7426] Losartan and its active metabolite bind the AT<sub>1</sub> receptor with 1000 times more affinity than they bind to the AT<sub>2</sub> receptor.[L7423,L7426] The active metabolite of losartan is 10-40 times more potent by weight than unmetabolized losartan as an inhibitor of AT<sub>1</sub> and is a non-competitive inhibitor.[L7423,L7426] Losartan''s prevention of angiotensin II binding causes vascular smooth muscle relaxation, lowering blood pressure.[L7423,L7426]



Angiotensin II would otherwise bind to the AT<sub>1</sub> receptor and induce vasoconstriction, raising blood pressure.[L7423,L7426]', 'Losartan is an angiotensin II receptor blocker used to treat hypertension, diabetic nephropathy, and to reduce the risk of stroke.[A1033,L7423,L7426] Losartan has a long duration of action as it is given once daily.[L7423,L7426] Patients taking losartan should be regularly monitored for hypotension, renal function, and potassium levels.[L7423,L7426]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26021, 'Rheumatizen topical cream 30 gm', 'روماتزين كريم 30جم', '35', '22.5', 'Analgesic', 'Camphor+menthol+methyl salicylate', 'Medizen pharmaceutical industries', 'Cream', 'كريم', '30 gm', '1', NULL, 'mechanism of action of camphor camphor acts as a rubefacient when it is rubbed on the skin and causes localized vasodilatation; which gives feelings of comfort and warmth. when applied gently on the skin it acts as an anti-pruritic agent and creates a fee', '6222012400010', NULL, 2408, '2024-07-27', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21850, 'Linezomentin 200mg/100ml i.v. inf. 300 ml', 'لينزومنتين 200مجم/100مل للتسريب الوريدي 300 مل', '164', '126', 'Antibiotic.oxazolidinone', 'Linezolid', 'Arabcomed-egypt', 'Unknown', 'غير محدد', '200mg', '1', NULL, 'about linezolid oxazolidinone derivative reversible nonselective monoamine oxidase inhibitor antibiotic. mechanism of action of linezolid linezolid binds to the 23 s fraction of 50 s ribosome and inhibit the formation of 70s initiation complex. indication', NULL, NULL, 1453, '2023-09-24', 'Linezolid is indicated in adults and children for the treatment of infections caused by susceptible Gram-positive bacteria, including nosocomial pneumonia, community-acquired pneumonia, skin and skin structure infections, and vancomycin-resistant _Enterococcus faecium_ infections.[L32965] Examples of susceptible bacteria include _Staphylococcus aureus_, _Streptococcus pneumoniae_, _Streptococcus pyogenes_, and _Streptococcus agalactiae_.[L32965]



Linezolid is not indicated for the treatment of Gram-negative infections, nor has it been evaluated for use longer than 28 days.[L32965]', 'Linezolid exerts its antibacterial effects by interfering with bacterial protein translation.[A233035] It binds to a site on the bacterial 23S ribosomal RNA of the 50S subunit and prevents the formation of a functional 70S initiation complex, which is essential for bacterial reproduction, thereby preventing bacteria from dividing.[L32965]



Point mutations in the bacterial 23S rRNA can lead to linezolid resistance, and the development of linezolid-resistant _Enterococcus faecium_ and _Staphylococcus aureus_ have been documented during its clinical use.[L32965] As antimicrobial susceptibility patterns are geographically distinct, local antibiograms should be consulted to ensure adequate coverage of relevant pathogens prior to use.', 'Linezolid is an oxazolidinone antibacterial agent effective against most strains of aerobic Gram-positive bacteria and mycobacteria. It appears to be bacteriostatic against both staphylococci and enterococci and bactericidal against most isolates of streptococci.[L32965] Linezolid has shown some _in vitro_ activity against Gram-negative and anaerobic bacteria but is not considered efficacious against these organisms.[L32965]



Linezolid is a reversible and non-selective inhibitor of monoamine oxidase (MAO) enzymes and can therefore contribute to the development of serotonin syndrome when administered alongside serotonergic agents such as selective serotonin re-uptake inhibitors (SSRIs) or tricyclic antidepressants (TCAs).[L32965] Linezolid should not be used for the treatment of catheter-related bloodstream infections or catheter-site infections, as the risk of therapy appears to outweigh its benefits under these circumstances.[L32965]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28519, 'Trinovume 750mg 10 f.c. tab.', 'ترينوفيوم 750مجم 10 اقراص', '46', NULL, 'Anti-epileptic.pyrrolidine', 'Levetiracetam', 'Sigma > hochster pharmaceutical industries', 'Tab', 'أقراص', '750mg', '1', NULL, 'about levetiracetam it is s-enantiomer of etiracetam. anticonvulsant medication used to treat epilepsy. mechanism of action of levetiracetam the exact mechanism for levetiracetam is unknown. however the drug binds to a synaptic vesicle protein sv2a which', NULL, NULL, 820, '2022-07-15', 'Levetiracetam is indicated as an adjunctive therapy in the treatment of partial onset seizures in epileptic patients who are one month of age and older. Additionally, it is indicated as an adjunct in the treatment of myoclonic seizures in patients with juvenile myoclonic epilepsy who are 12 years of age and older, and in primary generalized tonic-clonic seizures in patients with idiopathic generalized epilepsy who are 6 years of age and older.[L8606]



Levetiracetam is also available as an orally dissolvable tablet that is indicated as an adjunct in the treatment of partial onset seizures in patients with epilepsy who are 4 years of age and older and weigh more than 20kg.[L8609]', 'The exact mechanism through which levetiracetam exerts its anti-epileptic effects is unclear, but is thought to be unique amongst other anti-epileptic medications. Current knowledge suggests that levetiracetam’s binding to synaptic vesicle protein 2A (SV2A) is a key driver of its action. SV2A is a membrane-bound protein that is found on synaptic vesicles and is ubiquitous throughout the CNS[A12546] - it appears to play a role in vesicle exocytosis[L8606,L8615] and in the modulation of synaptic transmission by increasing the available amount of secretory vesicles available for neurotransmission.[A12552] Stimulation of pre-synaptic SV2A by levetiracetam may inhibit neurotransmitter release,[A12551] but this action does not appear to affect normal neurotransmission. This has led to the suggestion that levetiracetam exclusively modulates the function of SV2A only under pathophysiological conditions.[A12546] Levetiracetam and related analogues showed a correlation between affinity for SV2A and anti-epileptic potency, further suggesting that action at this site contributes to the anti-epileptic activity of the drug.[L8606,L8615]



Levetiracetam has also been shown to indirectly affect GABAergic neurotransmission (despite having no direct effect on GABAergic or glutamatergic receptors) and modulate ionic currents.[A12552] Similarly, levetiracetam has been shown in vitro to inhibit N-type calcium channels.[A16562] How, or even if, these actions are implicated in its anti-epileptic action have yet to be elucidated.', 'Levetiracetam appears to prevent seizure activity via the selective inhibition of hypersynchronized epileptiform burst firing without affecting normal neuronal transmission, though the exact mechanism through which this occurs is unclear.[L8606,L8615] The therapeutic index of levetiracetam is wide,[L8615,A185918] making it relatively unique amongst other anti-epileptic medications.



Anti-epileptic drugs, including levetiracetam, may increase the risk of suicidal ideation or behaviour - patients taking levetiracetam should be monitored for the emergence or worsening of depressive symptoms, suicidal ideation, and behavioural abnormalities.[L8606,L8600,L8615]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4114, 'Ema itopride 50 mg 30 tab', 'ايما ايتوبرايد 50مجم 30 قرص', '48', '30', 'Motility stimulant', 'Itopride', 'Pharopharma > ema pharm', 'Tab', 'أقراص', '50 mg', '3', NULL, NULL, '6224000912178', NULL, 2425, '2024-10-28', 'Investigated for use/treatment in gastrointestinal diseases and disorders (miscellaneous).', 'Itopride has anticholinesterase (AchE) activity as well as dopamine D2 receptor antagonistic activity. It is well established that M3 receptors exist on the smooth muscle layer throughout the gut and acetylcholine (ACh) released from enteric nerve endings stimulates the contraction of smooth muscle through M3 receptors. The enzyme AChE hydrolyses the released ACh, inactivates it and thus inhibits the gastric motility leading to various digestive disorders. Besides ACh, dopamine is present in significant amounts in the gastrointestinal tract and has several inhibitory effects on gastrointestinal motility, including reduction of lower esophageal sphincter and intragastric pressure. These effects appear to result from suppression of ACh release from the myenteric motor neurons and are mediated by the D2 subtype of dopamine receptors. Itopride, by virtue of its dopamine D2 receptor antagonism, removes the inhibitory effects on Ach release. It also inhibits the enzyme AchE which prevents the degradation of ACh. The net effect is an increase in ACh concentration, which in turn, promotes gastric motility, increases the lower esophageal sphincter pressure, accelerates gastric emptying and improves gastro-duodenal coordination. This dual mode of action of Itopride is unique and different from the actions of other prokinetic agents available in the market.

', NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23247, 'Naphcon a eye drops 15 ml', 'نافكون ايه قطرة عين 15 مل', '12', NULL, 'Corneal vascularity.antihistamine combined with alpha 1 agonist', 'Naphazoline+pheniramine', 'Rameda > alcon', 'Drops', 'نقط', '15 ml', '1', NULL, 'about naphazoline imidazoline derivative alpha-adrenergic agonist a decongestant vasoconstrictor. mechanism of action of naphazoline naphazoline exerts its vasoconstrictor action by acting as a selective alpha-1 adrenergic agonist. thus it reduces blood f', '6223003974220', NULL, 1099, '2022-06-18', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13952, 'Amiron 150mg/3ml 6 i.v. amp.', 'اميرون 150مجم/3مل 6 امبولات وريد', '12', NULL, 'Antiarrhythmias.class iii', 'Amiodarone', 'Sedico', 'Amp', 'أمبول', '150mg', '6', NULL, 'description an antianginal and antiarrhythmic drug. it increases the duration of ventricular and atrial muscle action by inhibiting na k-activated myocardial adenosine triphosphatase. there is a resulting decrease in heart rate and in vascular resistance.', NULL, NULL, 1772, '2022-07-01', 'The FDA approved indications for amiodarone are recurrent ventricular fibrillation (VF) and recurrent hemodynamically unstable ventricular tachycardia (VT). The FDA emphasizes that this drug should only be given in these conditions when they are clinically documented and have not responded to normal therapeutic doses of other antiarrhythmic agents, or when other drugs are not tolerated by the patient.[L3561]



Off-label indications include atrial fibrillation and supraventricular tachycardia.[A189666,A189720,A189723,L11286]', 'Amiodarone is considered a class III anti-arrhythmic drug. It blocks potassium currents that cause repolarization of the heart muscle during the third phase of the cardiac action potential. As a result amiodarone increases the duration of the action potential as well as the effective refractory period for cardiac cells (myocytes). Therefore, cardiac muscle cell excitability is reduced, preventing and treating abnormal heart rhythms.[A183137,A189747]



Unique from other members of the class III anti-arrhythmic drug class, amiodarone also interferes with the functioning of beta-adrenergic receptors, sodium channels, and calcium channels channels. These actions, at times, can lead to undesirable effects, such as hypotension, bradycardia, and Torsades de pointes (TdP).[L11265] In addition to the above, amiodarone may increase activity of peroxisome proliferator-activated receptors, leading to steatogenic changes in the liver or other organs.[A20227,A20650] Finally, amiodarone has been found to bind to the thyroid receptor due to its iodine content, potentially leading to amiodarone induced hypothyroidism or thyrotoxicosis.[A182168]', 'After intravenous administration, amiodarone acts to relax smooth muscles that line vascular walls, decreases peripheral vascular resistance (afterload), and increases the cardiac index by a small amount. Administration by this route also decreases cardiac conduction, preventing and treating arrhythmias.[A1901,A189666,L3561] When it is given orally, however, amiodarone does not lead to significant changes in the left ventricular ejection fraction. Similar to other anti-arrhythmic agents, controlled clinical trials do not confirm that oral amiodarone increases survival.[L3561] 



Amiodarone prolongs the QRS duration and QT interval.  In addition, a decreased SA (sinoatrial) node automaticity occurs with a decrease in AV node conduction velocity. Ectopic pacemaker automaticity is also inhibited.[L11265] Thyrotoxicosis or hypothyroidism may also result from the administration of amiodarone, which contains high levels of iodine, and interferes with normal thyroid function.[A182168]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (14904, 'Bambuterol 20 mg 10 tabs', 'بامبيتيرول 20مجم 10 اقراص', '25', NULL, 'Bronchodilator.long acting beta 2 agonist', 'Bambuterol', 'Arab caps', 'Tab', 'أقراص', '20 mg', '1', 'موسع للشعب الهوائية', 'long acting beta 2-adrenoceptor agonist anti asthmatic-bronchodilators. mechanism of action of bambuterol this prodrug of the 2-agonist terbutaline undergoes oxidation and hydrolysis in the body and converted into terbutaline. this terbutaline is a bronch', NULL, NULL, 801, '2022-05-30', 'For the prevention and reversal of bronchospasm in patients 12 years of age and older with asthma and reversible bronchospasm associated with bronchitis and emphysema.', 'The pharmacologic effects of bambuterol are at least in part attributable to stimulation through beta-adrenergic receptors (beta 2 receptors) of intracellular adenyl cyclase, the enzyme that catalyzes the conversion of adenosine triphosphate (ATP) to cyclic AMP. Increased cyclic AMP levels are associated with relaxation of bronchial smooth muscle and inhibition of release of mediators of immediate hypersensitivity from cells, especially from mast cells.', 'Bambuterol is a long acting beta2-adrenoceptor agonist used in the treatment of asthma. It is a prodrug of terbutaline. Bambuterol causes smooth muscle relaxation, resulting in dilation of bronchial passages.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21916, 'Lipovast 20mg 7 f.c.tab.', 'ليبوفاست 20 مجم 7 أقراص', '12', NULL, 'Antihyperlipidemic.statins', 'Atorvastatin', 'Misr', 'Tab', 'أقراص', '20mg', '1', NULL, 'description : is a member of the drug class known as statins. dose: adult po- initial- the recommended dose is 10 mg daily. maximum: 80 mg/day. indication : may be used as primary prevention in individuals with multiple risk factors for coronary heart dis', NULL, NULL, 990, '2022-07-12', 'Atorvastatin is indicated for the treatment of several types of dyslipidemias, including primary hyperlipidemia and mixed dyslipidemia in adults, hypertriglyceridemia, primary dysbetalipoproteinemia, homozygous familial hypercholesterolemia, and heterozygous familial hypercholesterolemia in adolescent patients with failed dietary modifications.[A177397]



Dyslipidemia describes an elevation of plasma cholesterol, triglycerides or both as well as to the presence of low levels of high-density lipoprotein. This condition represents an increased risk for the development of atherosclerosis.[L6025] 



Atorvastatin is indicated, in combination with dietary modifications, to prevent cardiovascular events in patients with cardiac risk factors and/or abnormal lipid profiles.[A177397]



Atorvastatin can be used as a preventive agent for myocardial infarction, stroke, revascularization, and angina, in patients without coronary heart disease but with multiple risk factors and in patients with type 2 diabetes without coronary heart disease but multiple risk factors.[A177397]



Atorvastatin may be used as a preventive agent for non-fatal myocardial infarction, fatal and non-fatal stroke, revascularization procedures, hospitalization for congestive heart failure and angina in patients with coronary heart disease.[A177397]



Prescribing of statin medications is considered standard practice following any cardiovascular events and for people with a moderate to high risk of development of CVD. Statin-indicated conditions include diabetes mellitus, clinical atherosclerosis (including myocardial infarction, acute coronary syndromes, stable angina, documented coronary artery disease, stroke, trans ischemic attack (TIA), documented carotid disease, peripheral artery disease, and claudication), abdominal aortic aneurysm, chronic kidney disease, and severely elevated LDL-C levels.[A181087, A181406]', 'Atorvastatin is a statin medication and a competitive inhibitor of the enzyme HMG-CoA (3-hydroxy-3-methylglutaryl coenzyme A) reductase, which catalyzes the conversion of HMG-CoA to mevalonate, an early rate-limiting step in cholesterol biosynthesis.[A177388,A181421] Atorvastatin acts primarily in the liver, where decreased hepatic cholesterol concentrations stimulate the upregulation of hepatic low-density lipoprotein (LDL) receptors, which increases hepatic uptake of LDL. Atorvastatin also reduces Very-Low-Density Lipoprotein-Cholesterol (VLDL-C), serum triglycerides (TG) and Intermediate Density Lipoproteins (IDL), as well as the number of apolipoprotein B (apo B) containing particles, but increases High-Density Lipoprotein Cholesterol (HDL-C). 



_In vitro_ and _in vivo_ animal studies also demonstrate that atorvastatin exerts vasculoprotective effects independent of its lipid-lowering properties, also known as the pleiotropic effects of statins.[A181424] These effects include improvement in endothelial function, enhanced stability of atherosclerotic plaques, reduced oxidative stress and inflammation, and inhibition of the thrombogenic response. Statins were also found to bind allosterically to β2 integrin function-associated antigen-1 (LFA-1), which plays an essential role in leukocyte trafficking and T cell activation.[A181559]', 'Atorvastatin is an oral antilipemic agent that reversibly inhibits HMG-CoA reductase. It lowers total cholesterol, low-density lipoprotein-cholesterol (LDL-C), apolipoprotein B (apo B), non-high density lipoprotein-cholesterol (non-HDL-C), and triglyceride (TG) plasma concentrations while increasing HDL-C concentrations. High LDL-C, low HDL-C and high TG concentrations in the plasma are associated with increased risk of atherosclerosis and cardiovascular disease. The total cholesterol to HDL-C ratio is a strong predictor of coronary artery disease, and high ratios are associated with a higher risk of disease. Increased levels of HDL-C are associated with lower cardiovascular risk. By decreasing LDL-C and TG and increasing HDL-C, atorvastatin reduces the risk of cardiovascular morbidity and mortality.[A174580,A181087,A181406,A181925] 



Elevated cholesterol levels (and high low-density lipoprotein (LDL) levels in particular) are an important risk factor for the development of CVD.[A181087] Clinical studies have shown that atorvastatin reduces LDL-C and total cholesterol by 36-53%.[A177415] In patients with dysbetalipoproteinemia, atorvastatin reduced the levels of intermediate-density lipoprotein cholesterol.[A177397] It has also been suggested that atorvastatin can limit the extent of angiogenesis, which can be useful in the treatment of chronic subdural hematoma.[A177388]



**Myopathy/Rhabdomyolysis**



Atorvastatin, like other HMG-CoA reductase inhibitors, is associated with a risk of drug-induced myopathy characterized by muscle pain, tenderness, or weakness in conjunction with elevated levels of creatine kinase (CK). Myopathy often manifests as rhabdomyolysis with or without acute renal failure secondary to myoglobinuria. The risk of statin-induced myopathy is dose-related, and the symptoms of myopathy are typically resolved upon drug discontinuation. Results from observational studies suggest that 10-15% of people taking statins may experience muscle aches at some point during treatment.[A182258]



**Liver Dysfunction**



Statins, like some other lipid-lowering therapies, have been associated with biochemical abnormalities of liver function. Persistent elevations (> 3 times the upper limit of normal [ULN] occurring on two or more occasions) in serum transaminases occurred in 0.7% of patients who received atorvastatin in clinical trials. This effect appears to be dose-related.[F4670,F4673]



**Endocrine Effects**



Statins are associated with a risk of increased serum HbA1c and glucose levels. An _in vitro_ study demonstrated a dose-dependent cytotoxic effect on human pancreatic islet β cells following treatment with atorvastatin. Moreover, insulin secretion rates decreased relative to control.[A182012]



HMG-CoA reductase inhibitors interfere with cholesterol synthesis and may theoretically interfere with the production of adrenal and/or gonadal steroids. Clinical studies with atorvastatin and other HMG-CoA reductase inhibitors have suggested that these agents do not affect plasma cortisol concentrations, basal plasma testosterone concentration, or adrenal reserve. However, the effect of statins on male fertility has not been fully investigated. The effects of statins on the pituitary-gonadal axis in premenopausal women are unknown.[F4673]



**Cardiovascular**



Significant decreases in circulating ubiquinone levels in patients treated with atorvastatin and other statins have been observed. The clinical significance of a potential long-term statin-induced deficiency of ubiquinone has not been established. It has been reported that a decrease in myocardial ubiquinone levels could lead to impaired cardiac function in patients with borderline congestive heart failure.[F4673]



**Lipoprotein A**



In some patients, the beneficial effect of lowered total cholesterol and LDL-C levels may be partly blunted by the concomitant increase in Lp(a) lipoprotein concentrations. Present knowledge suggests the importance of high Lp(a) levels as an emerging risk factor for coronary heart disease.[F4673] Further studies have demonstrated statins affect Lp(a) levels differently in patients with dyslipidemia depending on their apo(a) phenotype; statins increase Lp(a) levels exclusively in patients with the low molecular weight apo(a) phenotype.[A181418]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20723, 'Imatib 100 mg 10 caps.', 'ايماتيب 100 مجم 10 كبسولات', '250', NULL, 'Anticancer', 'Imatinib', 'Cipla ltd. - india > star international company', 'Cap', 'كبسولة', '100 mg', '1', NULL, 'about imatinib anticancer tyrosine- kinase inhibitor first generation. mechanism of action of imatinib imatinib is an inhibitor of multiple tyrosine kinases including kit abl and pdgfr( platelet-derived growth factor receptor) and inhibit phosphorelation.', NULL, NULL, 955, '2022-08-02', 'Imatinib is indicated for the treatment of adult and pediatric chronic myeloid leukemia with Philadelphia chromosome mutation (Ph+) in blast crisis, accelerated phase, or chronic phase after IFN-alpha therapy failure.[L42080] Additionally, imatinib is also indicated to treat adult and pediatric Ph+ acute lymphoblastic leukemia, adult myelodysplastic/myeloproliferative diseases, adult aggressive systemic mastocytosis, adult hypereosinophilic syndrome and/or chronic eosinophilic leukemia (CEL), adult dermatofibrosarcoma protuberans, and malignant gastrointestinal stromal tumors (GIST).[L42080]

', 'Imatinib mesylate is a protein-tyrosine kinase inhibitor that inhibits the BCR-ABL tyrosine kinase, the constitutively active tyrosine kinase created by the Philadelphia chromosome abnormality in CML.[L42080]Although the function of normal BCR is still unclear, ABL activation is overexpressed in various tumors and is heavily implicated in cancer cells growth and survival.[A249305,A249310] Imatinib inhibits the BCR-ABL protein by binding to the ATP pocket in the active site, thus preventing downstream phosphorylation of target protein.[A249305]



Imatinib is also an inhibitor of the receptor tyrosine kinases for platelet-derived growth factor (PDGF) and stem cell factor

(SCF), c-Kit, and inhibits PDGF- and SCF-mediated cellular events. In vitro, imatinib inhibits proliferation and induces

apoptosis in GIST cells, which express an activating c-Kit mutation.[L42080]', 'Imatinib is a 2-phenylaminopyrimidine derivative neoplastic agent that belongs to the class of tyrosine kinase inhibitors.[L42080] Although imatinib inhibits a number of tyrosine kinases, it is quite selective toward the BCR-ABL fusion protein that is present in various cancers.[A249305] BCR-ABL pathway controls many downstream pathways that are heavily implicated in neoplastic growth such as the Ras/MapK pathway (cellular proliferation), Src/Pax/Fak/Rac pathway (cellular motility), and PI/PI3K/AKT/BCL-2 pathway (apoptosis pathway).[A249290, A249295, A249300] Therefore, the BCR-ABL pathway is an attractive target for cancer treatment. Although normal cells also depend on these pathways for growth, these cells tend to have redundant tyrosine kinases to continually function in spite of ABL inhibition from imatinib.[A943] Cancer cells, on the other hand, can have a dependence on BCR-ABL, thus more heavily impacted by imatinib.[A943]

 ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31580, 'Vildabetes 50/1000mg 30 tabs.', 'فيلدابيتس 50/1000مجم 30 قرص', '186', '141', 'Anti-diabetic.combined secretagogues+sensitizers', 'Metformin hydrochloride+vildagliptin', 'Egpi', 'Tab', 'أقراص', '1000mg', '3', 'تركيبة علاجية لعلاج داء السكرى', 'about metformin biguanide derivative oral anti-diabetic. mechanism of action of metformin it is a biguanide which exerts antidiabetic action. the drug suppresses gluconeogenesis in liver and thus suppresses hepatic glucose output. it enhance insulin media', '6223003934606', NULL, 2898, '2024-09-29', '**Metformin immediate-release formulations**

 

Metformin is indicated as an adjunct to diet and exercise to improve glycemic control in adults and pediatric patients ≥10 years old with type 2 diabetes mellitus.[L40243]



**Metformin extended-release tablet (XR)**



The extended-release formulation of metformin is indicated as an adjunct to diet and exercise to improve glycemic control in adults with type 2 diabetes mellitus. Safety in children has not been determined to this date.[L12207]



**Metformin combination products**



Metformin is a component of a variety of combination products with other anti-diabetic agents. It is indicated, along with diet and exercise, to improve glycemic control in adult patients with type 2 diabetes mellitus in combination with DPP-4 inhibitors ([sitagliptin], [linagliptin], [alogliptin], or [saxagliptin]),[L11479,L40248,L40253,L40258,L40263] in combination with SGLT2 inhibitors ([canagliflozin], [empagliflozin], [ertugliflozin], or [dapagliflozin]),[L11479,L30503,L1134,L13679,L38729] or in combination with [pioglitazone].[L11419]', 'Metformin''s mechanisms of action are unique from other classes of oral antihyperglycemic drugs. Metformin decreases blood glucose levels by decreasing hepatic glucose production (also called gluconeogenesis), decreasing the intestinal absorption of glucose, and increasing insulin sensitivity by increasing peripheral glucose uptake and utilization.[L12207] It is well established that metformin inhibits mitochondrial complex I activity, and it has since been generally postulated that its potent antidiabetic effects occur through this mechanism.[A36534, A36557]   The above processes lead to a decrease in blood glucose, managing type II diabetes and exerting positive effects on glycemic control.



After ingestion, the organic cation transporter-1 (OCT1) is responsible for the uptake of metformin into hepatocytes (liver cells). As this drug is positively charged, it accumulates in cells and in the mitochondria because of the membrane potentials across the plasma membrane as well as the mitochondrial inner membrane. Metformin inhibits mitochondrial complex I, preventing the production of mitochondrial ATP leading to increased cytoplasmic ADP:ATP and AMP:ATP ratios.[A36534] These changes activate AMP-activated protein kinase (AMPK), an enzyme that plays an important role in the regulation of glucose metabolism.[A176348] Aside from this mechanism, AMPK can be activated by a lysosomal mechanism involving other activators.  Following this process, increases in AMP:ATP ratio also inhibit _fructose-1,6-bisphosphatase_ enzyme, resulting in the inhibition of gluconeogenesis, while also inhibiting _adenylate cyclase_ and decreasing the production of cyclic adenosine monophosphate (cAMP),[A36534] a derivative of ATP used for cell signaling [A176351]. Activated AMPK phosphorylates two isoforms of acetyl-CoA carboxylase enzyme, thereby inhibiting fat synthesis and leading to fat oxidation, reducing hepatic lipid stores and increasing liver sensitivity to insulin.[A36534] 



In the intestines, metformin increases anaerobic glucose metabolism in enterocytes (intestinal cells), leading to reduced net glucose uptake and increased delivery of lactate to the liver. Recent studies have also implicated the gut as a primary site of action of metformin and suggest that the liver may not be as important for metformin action in patients with type 2 diabetes. Some of the ways metformin may play a role on the intestines is by promoting the metabolism of glucose by increasing glucagon-like peptide I (GLP-1) as well as increasing gut utilization of glucose.[A36534] 



In addition to the above pathway, the mechanism of action of metformin may be explained by other ways, and its exact mechanism of action has been under extensive study in recent years.[A36535, A36554, A36555, A36557]', '**General effects**



Insulin is an important hormone that regulates blood glucose levels.[T514] Type II diabetes is characterized by a decrease in sensitivity to insulin, resulting in elevations in blood glucose when the pancreas can no longer compensate. In patients diagnosed with type 2 diabetes, insulin is unable to exert adequate effects on tissues and cells (i.e. insulin resistance)[T514] and insulin deficiency may also be present.[L5753]



Metformin reduces hepatic production of glucose, decreases the intestinal absorption of glucose, and enhances insulin sensitivity by increasing both peripheral glucose uptake and utilization. In contrast with drugs of the sulfonylurea class, which lead to hyperinsulinemia, the secretion of insulin is unchanged with metformin use.[L12207]



**Effect on fasting plasma glucose (FPG) and Glycosylated hemoglobin (HbA1c)**



HbA1c is an important periodic measure of glycemic control used to monitor diabetic patients. Fasting plasma glucose is also a useful and important measure of glycemic control. In a 29-week clinical trial of subjects diagnosed with type II diabetes, metformin decreased the fasting plasma glucose levels by an average of 59 mg/dL from baseline, compared to an average increase of 6.3 mg/dL from baseline in subjects taking a placebo.[L12207]  Glycosylated hemoglobin (HbA1c) was decreased by about 1.4% in subjects receiving metformin, and increased by 0.4% in subjects receiving placebo only.[L12207]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (18558, 'Esmorap 20 mg 7 sachets', 'ازموراب 20 مجم 7 اكياس', '63', '45.5', 'Peptic ulcer.proton pump inhibitor', 'Esomeprazole', 'Aug pharma', 'Sachet', 'أكياس', '20 mg', '1', NULL, 'about esomeprazole a proton pump inhibitor substituted benzimidazole antiulcer antisecretory drug. mechanism of action of esomeprazole esomeprazole is the s enantiomer of omeprazole and is a proton pump inhibitor. it is a prodrug. after administration it', '6223005464309', NULL, 1341, '2024-11-08', 'Esomeprazole is indicated for the treatment of acid-reflux disorders including healing and maintenance of erosive esophagitis, and symptomatic gastroesophageal reflux disease (GERD), peptic ulcer disease, H. pylori eradication, prevention of gastrointestinal bleeds with NSAID use, and for the long-term treatment of pathological hypersecretory conditions including Zollinger-Ellison Syndrome. ', 'Esomeprazole exerts its stomach acid-suppressing effects by preventing the final step in gastric acid production by covalently binding to sulfhydryl groups of cysteines found on the (H+, K+)-ATPase enzyme at the secretory surface of gastric parietal cells. This effect leads to inhibition of both basal and stimulated gastric acid secretion, irrespective of the stimulus. As the binding of esomeprazole to the (H+, K+)-ATPase enzyme is irreversible and new enzyme needs to be expressed in order to resume acid secretion, esomeprazole''s duration of antisecretory effect that persists longer than 24 hours.[FDA Label]

', 'Esomeprazole is a compound that inhibits gastric acid secretion and is indicated in the treatment of gastroesophageal reflux disease (GERD), the healing of erosive esophagitis, and <i>H. pylori</i> eradication to reduce the risk of duodenal ulcer recurrence. Esomeprazole belongs to a new class of antisecretory compounds, the substituted benzimidazoles, that do not exhibit anticholinergic or H2 histamine antagonistic properties, but that suppress gastric acid secretion by specific inhibition of the H<sup>+</sup>/K<sup>+</sup> ATPase at the secretory surface of the gastric parietal cell. By doing so, it inhibits acid secretion into the gsatric lumen. This effect is dose-related and leads to inhibition of both basal and stimulated acid secretion irrespective of the stimulus.



Esomeprazole is the s-isomer of [DB00338], which is a racemate of the S- and R-enantiomer. Esomeprazole has been shown to inhibit acid secretion to a similar extent as [DB00338], without any significant differences between the two compounds _in vitro_. 



PPIs such as esomeprazole have also been shown to inhibit the activity of dimethylarginine dimethylaminohydrolase (DDAH), an enzyme necessary for cardiovascular health. DDAH inhibition causes a consequent accumulation of the nitric oxide synthase inhibitor asymmetric dimethylarginie (ADMA), which is thought to cause the association of PPIs with increased risk of cardiovascular events in patients with unstable coronary syndromes.[A177577, A177580]



Due to their good safety profile and as several PPIs are available over the counter without a prescription, their current use in North America is widespread. Long term use of PPIs such as esomeprazole has been associated with possible adverse effects, however, including increased susceptibility to bacterial infections (including gastrointestinal _C. difficile_), reduced absorption of micronutrients including iron and B12, and an increased risk of developing hypomagnesemia and hypocalcemia which may contribute to osteoporosis and bone fractures later in life.[A177571]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28470, 'Triaxone 1gm i.m. vial', 'ترياكسون 1جم عضل فيال', '49', NULL, 'Antibiotic.cephalosporin.third-generation', 'Ceftriaxone', 'Tabuk pharmaceutical manufacturing company', 'Vial', 'فيال', '1gm', '1', NULL, 'about ceftriaxone third generation cephalosporin antibiotic(parenteral). mechanism of action of ceftriaxone ceftriaxone is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiti', '6285147004911', NULL, 1052, '2022-07-11', 'Ceftriaxone is used for the treatment of the infections (respiratory, skin, soft tissue, UTI, ENT) caused by susceptible organisms.[L15082] Organisms that are generally susceptible to ceftriaxone include S. pneumoniae, S. pyogenes (group A beta-hemolytic streptococci), coagulase-negative staphylococci, Some Enterobacter spp, H. influenzae, N. gonorrhoeae, P. mirabilis, E. coli, Klebsiella spp, M. catarrhalis, B. burgdorferi, and some oral anaerobes.[A215582] ', 'Ceftriaxone works by inhibiting the mucopeptide synthesis in the bacterial cell wall.[T324,L15082] The beta-lactam moiety of ceftriaxone binds to carboxypeptidases, endopeptidases, and transpeptidases in the bacterial cytoplasmic membrane. These enzymes are involved in cell-wall synthesis and cell division. Binding of ceftriaxone to these enzymes causes the enzyme to lose activity; therefore, the bacteria produce defective cell walls, causing cell death.', 'Ceftriaxone is a cephalosporin/cephamycin beta-lactam antibiotic used in the treatment of bacterial infections caused by susceptible, usually gram-positive, organisms.[L15082] Ceftriaxone has <i>in vitro</i> activity against gram-positive aerobic, gram-negative aerobic, and anaerobic bacteria.[L15087] The bactericidal activity of ceftriaxone results from the inhibition of cell wall synthesis and is mediated through ceftriaxone binding to penicillin-binding proteins (PBPs).[T324] Ceftriaxone is stable against hydrolysis by a variety of beta-lactamases, including penicillinases, and cephalosporinases and extended-spectrum beta-lactamases.[L15082] However, resistance to ceftriaxone usually occurs through beta-lactamase hydrolysis, altered PBPs, or reduced bacterial cell permeability.[T324] Ceftriaxone should not be mixed with or giving in the same IV line as diluents/products containing calcium as they may cause ceftriaxone to precipitate.[L15082] Ceftriaxone use may also cause biliary sludge or gallbladder pseudolithiasis.[L15082,A215602] ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21650, 'Laxeol pi 7.5mg/ml oral drops 15ml', 'لاكسيول بي 7.5مجم/مل نقط 15 مل', '12', '5', 'Laxative', 'Sodium picosulphate', 'Eva pharma', 'Drops', 'نقط', '7.5mg', '1', NULL, 'about sodium picosulphate a locally acting bowel preparation a stimulant laxative. mechanism of action of sodium picosulphate sodium picosulphate is a stimulant laxative. after oral administration it is activated by the bacteria present in the large intes', NULL, NULL, 970, '2024-12-20', 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15151, 'Betaval 0.1% oint. 15 gm', 'بيتافال 0.1% مرهم 15 جم', '15', '6.75', 'Glucocorticoid', 'Betamethasone', 'Arab drug company.', 'Oint', 'مرهم', '0.1%', '1', NULL, 'about betamethasone potent glucocorticoid anti-inflammatory immunosuppressent. mechanism of action of betamethasone the drug exerts it`s pharmacological action by penetrating and binding to cytoplasmic receptor protein and causes a structural change in st', '6221060001477', NULL, 1045, '2025-10-25', 'As a member of the corticosteroid family, betamethasone is indicated for the treatment of several inflammatory conditions. As topical monotherapy, betamethasone is indicated to relieve pruritic and inflammatory symptoms of corticosteroid-responsive-dermatoses.[L11991,L10782,L11997] Betamethasone can be used topically in combination with a vitamin D analog such as calcipotriene to treat plaque psoriasis.[L12000] The corticosteroid is also available as an injectable suspension and can be used to manage a range of inflammatory conditions including endocrine disorders, gastrointestinal disorders, and rheumatic disorders among other conditions.[L11994]

', 'Glucocorticoids inhibit neutrophil apoptosis and demargination, and inhibit NF-Kappa B and other inflammatory transcription factors.[A187463] They also inhibit phospholipase A2, leading to decreased formation of arachidonic acid derivatives.[A187463] In addition, glucocorticoids promote anti-inflammatory genes like interleukin-10.[A187463]



Corticosteroids like betamethasone can act through nongenomic and genomic pathways.[A31458] The genomic pathway is slower and occurs when glucocorticoids activate glucocorticoid receptors and initiate downstream effects that promote transcription of anti-inflammatory genes including phosphoenolpyruvate carboxykinase (PEPCK), IL-1-receptor antagonist, and tyrosine amino transferase (TAT).[A31458] On the other hand, the nongenomic pathway is able to elicit a quicker response by modulating T-cell, platelet and monocyte activity through the use of existing membrane-bound receptors and second messengers.[A31458]', 'Corticosteroids bind to the glucocorticoid receptor inhibiting pro-inflammatory signals, while promoting anti-inflammatory signals.[A187463] Corticosteroids have a wide therapeutic window as patients may require doses that are multiples of what the body naturally produces.[A187463] Patients who require long-term treatment with a corticosteroid should be counselled regarding the risk of hypothalamic-pituitary-adrenal axis suppression and increased susceptibility to infections.[A187463]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2665, 'Cisplatin 1mg/ml (50mg) vial', 'سيسبلاتين 1مجم/مل (50مجم) فيال', '58', NULL, 'Antineoplastic', 'Cisplatin', 'Hospira inc.-uk > pfizer scientific office', 'Vial', 'فيال', '1mg', '1', NULL, 'description cisplatin cisplatinum or cis-diamminedichloroplatinum(ii) (cddp) is a platinum-based chemotherapy drug used to treat various types of cancers including sarcomas some carcinomas (e.g. small cell lung cancer and ovarian cancer) lymphomas and ger', NULL, NULL, 1000, '2023-05-19', 'For the treatment of metastatic testicular tumors, metastatic ovarian tumors and advanced bladder cancer.', 'Alkylating agents work by three different mechanisms: 1) attachment of alkyl groups to DNA bases, resulting in the DNA being fragmented by repair enzymes in their attempts to replace the alkylated bases, preventing DNA synthesis and RNA transcription from the affected DNA, 2) DNA damage via the formation of cross-links (bonds between atoms in the DNA) which prevents DNA from being separated for synthesis or transcription, and 3) the induction of mispairing of the nucleotides leading to mutations.', 'Cisplatin is an antineoplastic in the class of alkylating agents and is used to treat various forms of cancer. Alkylating agents are so named because of their ability to add alkyl groups to many electronegative groups under conditions present in cells. They stop tumor growth by cross-linking guanine bases in DNA double-helix strands - directly attacking DNA. This makes the strands unable to uncoil and separate. As this is necessary in DNA replication, the cells can no longer divide. In addition, these drugs add methyl or other alkyl groups onto molecules where they do not belong which in turn inhibits their correct utilization by base pairing and causes a miscoding of DNA. Alkylating agents are cell cycle-nonspecific. Alkylating agents work by three different mechanisms all of which achieve the same end result - disruption of DNA function and cell death.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (26756, 'Selsun blue 207 ml moisturiing shampoo (n/a)', 'سيلسن بلو 207مل شامبو', '48', NULL, 'Hair care', 'Selenium sulfide', 'Famar > ramco', 'Amp', 'أمبول', '207 ml', '1', NULL, 'anti-dandruff shampoo', NULL, NULL, 640, '2022-07-24', 'For treatment of tinea versicolor, tinea capitis, dandruff and seborrheic dermatitis of the scalp.', 'Topical selenium sulfide may act by an antimitotic action, resulting in a reduction in the turnover of epidermal cells. It also has local irritant, antibacterial, and mild antifungal activity, which may contribute to its effectiveness. An antimitotic mechanism of action is suggested by data showing that selenium sulfide decreases the rate of incorporation of radioactively labeled thymidine into the DNA of dermal epithelial cells. The following organisms are generally considered susceptible to selenium sulfide in vitro: <i>Malassezia furfur</i>, <i>Microsporum sp.</i> including <i>Microsporum audouinii</i> and <i>Microsporum canis</i>, <i>Pityrosporon sp.</i>, <i>Trichophyton sp.</i> including <i>Trichophyton schoenleinii</i> and <i>Trichophyton tonsurans</i>. Selenium sulfide has been shown to be sporicidal to <i>T. tonsurans</i>, the most common etiologic agent of tinea capitis. One in-vitro study demonstrated that 2.5% selenium sulfide was equivalent in sporicidal activity to both 1% and 2% zinc pyrithione.', 'Selenium sulfide is an antifungal agent often used in shampoos for the treatment of dandruff and seborrheic dermatitis. Selenium sulfide is highly active in inhibiting the growth of <i>P. ovale</i>. It is also a proven cytostatic agent, slowing the growth of both hyperproliferative and normal cells in dandruff and seborrheic dermatitis. A 0.6% micronized form of selenium sulfide is also safe and effective for dandruff.', 'DrugBank', 0, 0, 0);
