INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (2548, 'Cibacen 5 mg 7 tab', 'سيباسين 5مجم 7 اقراص', '16', NULL, 'Antihypertensive ace', 'Benazepril', 'Novartis', 'Tab', 'أقراص', '5 mg', '1', NULL, NULL, NULL, NULL, 861, '2022-09-22', 'Benazepril is indicated for the treatment of hypertension[A840]. It may be used alone or in combination with thiazide diuretics[FDA Label].', 'Benazeprilat, the active metabolite of Benazepril, competes with angiotensin I for binding at the angiotensin-converting enzyme, blocking the conversion of angiotensin I to angiotensin II[FDA Label]. Inhibition of ACE results in decreased plasma angiotensin II[FDA Label]. As angiotensin II is a vasoconstrictor and a negative-feedback mediator for renin activity, lower concentrations result in a decrease in blood pressure and stimulation of baroreceptor reflex mechanisms, which leads to decreased vasopressor activity and to decreased aldosterone secretion[FDA Label].', 'Benazepril, an angiotensin-converting enzyme (ACE) inhibitor, is a prodrug which, when hydrolyzed by esterases to its active Benazeprilat[A836], is used to treat hypertension and heart failure, to reduce proteinuria and renal disease in patients with nephropathies, and to prevent stroke, myocardial infarction, and cardiac death in high-risk patients[A840,A838,A837]. Benazepril and Benazeprilat inhibit angiotensin-converting enzyme (ACE) in human subjects and animals[A840,A836]. ACE is a peptidyl dipeptidase that catalyzes the conversion of angiotensin I to the vasoconstrictor substance, angiotensin II[FDA Label]. Angiotensin II also stimulates aldosterone secretion by the adrenal cortex[FDA Label].', 'DrugBank', 0, 0, 0);
