INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28129, 'Thiopental sodium-up pharma 1 gm iv vial', 'ثيوبنتال صوديوم يو بي فارما 1جم فيال', '7', NULL, 'General anaesthesia', 'Thiopental', 'Up pharma', 'Vial', 'فيال', '1 gm', '1', NULL, 'indication for use as the sole anesthetic agent for brief (15 minute) procedures for induction of anesthesia prior to administration of other anesthetic agents to supplement regional anesthesia to provide hypnosis during balanced anesthesia with other age', NULL, NULL, 899, '2022-08-03', 'For use as the sole anesthetic agent for brief (15 minute) procedures, for induction of anesthesia prior to administration of other anesthetic agents, to supplement regional anesthesia, to provide hypnosis during balanced anesthesia with other agents for analgesia or muscle relaxation, for the control of convulsive states during or following inhalation anesthesia or local anesthesia, in neurosurgical patients with increased intracranial pressure, and for narcoanalysis and narcosynthesis in psychiatric disorders.', 'Thiopental binds at a distinct binding site associated with a Cl<sup>-</sup> ionopore at the GABA<sub>A</sub> receptor, increasing the duration of time for which the Cl<sup>-</sup> ionopore is open. The post-synaptic inhibitory effect of GABA in the thalamus is, therefore, prolonged.', 'Thiopental, a barbiturate, is used for the induction of anesthesia prior to the use of other general anesthetic agents and for induction of anesthesia for short surgical, diagnostic, or therapeutic procedures associated with minimal painful stimuli. Thiopental is an ultrashort-acting depressant of the central nervous system which induces hypnosis and anesthesia, but not analgesia. It produces hypnosis within 30 to 40 seconds of intravenous injection. Recovery after a small dose is rapid, with some somnolence and retrograde amnesia. Repeated intravenous doses lead to prolonged anesthesia because fatty tissues act as a reservoir; they accumulate Pentothal in concentrations 6 to 12 times greater than the plasma concentration, and then release the drug slowly to cause prolonged anesthesia', 'DrugBank', 0, 0, 0);
