INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17643, 'Diflustero 0.05% eye emulsion 5 ml', 'ديفلوستيرو 0.05% نقط للعين 5 مل', '45', '45', 'Glucocorticoid', 'Difluprednate', 'Orchidia pharmaceutical industries', 'Unknown', 'غير محدد', '0.05%', '1', NULL, 'difluprednate is a corticosteroid it is chemically a butyrate ester of 6(alpha) 9(alpha)-difluoro prednisolone acetate. accordingly difluprednate is sometimes abbreviated dfba for difluoroprednisolone butyrate acetate. difluprednate emulsion is used for:', '6224000219802', NULL, 1323, '2024-09-16', 'For the treatment of inflammation and pain associated with ocular surgery.', 'Corticosteroids are thought to act by the induction of phospholipase A2 inhibitory proteins (lipocortins). It is postulated that these proteins control the biosynthesis of potent mediators of infammation such as prostaglandins and leukotrienes by inhibiting the release of their common precursor arachidonic acid. Arachidonic acid is released from membrane phospholipids by phospholipase A2.', 'Difluprednate is a corticosteroid used as an anti-inflammatory steroidal drug used primarily in ocular surgery.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4017, 'Effox 20mg 20 tab', 'ايفوكس 20مجم 20 قرص', '10', NULL, 'Anti-ischemic', 'Isosorbide mononitrate', 'Minapharm', 'Tab', 'أقراص', '20mg', '2', NULL, NULL, '6222003700112', NULL, 1252, '2022-12-04', 'For the prevention of angina pectoris due to coronary artery disease.', 'Isosorbide dinitrate is converted to the active nitric oxide to activate guanylate cyclase. This activation increases levels of cyclic guanosine 3'',5''-monophosphate (cGMP). cGMP activates protein kinases and causes a series of phosphorylation reactions which leads to dephosphorylation of myosin light chains of smooth muscle fibres. Finally there is a release of calcium ions which causes smooth muscle relaxation and vasodilation.', 'Isosorbide Dinitrate is a moderate to long acting oral organic nitrate used for the relief and prophylactic management of angina pectoris. It relaxes the vascular smooth muscle and consequent dilatation of peripheral arteries and veins, especially the latter. Dilatation of the veins promotes peripheral pooling of blood and decreases venous return to the heart, thereby reducing left ventricular end- diastolic pressure and pulmonary capillary wedge pressure (preload). Arteriolar relaxation reduces systemic vascular resistance, systolic arterial pressure, and mean arterial pressure.', 'DrugBank', 0, 0, 0);
