        has_drug_interaction, has_food_interaction, has_disease_interaction
    ) VALUES (25425, 'Protofix 40mg 14 f.c. tab', 'بروتوفيكس 40مجم 14 قرص', '116', '80', 'Peptic ulcer.proton pump inhibitor', 'Pantoprazole', 'October pharma > rameda', 'Tab', 'أقراص', '40mg', '2', 'لعلاج الحموضة وقرحة المعدة', '

**Pantoprazole Injection**:



**Treatment of gastroesophageal reflux disease associated with a history of erosive esophagitis**



