INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (27683, 'Sunnypyramid 10mg/2ml 50 amp.', 'صنيبيراميد 10مجم/2مل 50 امبول', '400', '185', 'Antiemetic', 'Metoclopramide', 'Sunny medical', 'Amp', 'أمبول', '10mg', '50', NULL, 'about metoclopramide it is a dopamine antagonist - antiemetic gastroprokinetic agent.smechanism of action of metoclopramidesmetoclopramide acts through both serotonergic and dopaminergic receptors. metoclopramide binds to serotonin (5 ht3 &5ht4) and dopam', NULL, NULL, 1144, '2024-12-20', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Diabetic gastroparesis is a condition that causes frequent nausea and vomiting, which has a negative impact on quality of life and poses a significant burden on the healthcare system.[A184934] Metoclopramide is a dopamine antagonist used to treat nausea and vomiting that may be associated with diabetic gastroparesis in addition to gastroesophageal reflux disease (GERD). It can also be used to prevent nausea or vomiting associated with chemotherapy or certain surgical or diagnostic procedures.[L8417] 

One unique property of this drug is that it does not increase gastric acid secretion. It is available in the oral tablet form or in solution, and can also be administered through the intravenous route.[T683] Metoclopramide was initially approved by the FDA in 1980.[A184922]', '["A03FA01"]', '[]');
