INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tineacure 1% paint 15 ml', 11445, 'تينياكيور مسة للجلد 15 مل', '16', '10', 'Antifungals.thiocarbamate', 'Antifungals.thiocarbamate', NULL, 'Tolnaftate', 'Kahira', 'Paint', 'مس/دهان', '1%', '1', NULL, NULL, 'about tolnaftate thiocarbamate derivative antifungal. mechanism of action of tolnaftate tolnaftate is a reversible noncompetitive inhibitor of squalene epoxidase enzyme of fungi. this epoxidase enzyme is useful for the conversion of squalene to sterols. i', '6221068503188', 959, '2025-01-09', NULL, 0, 'Tolnaftate topical is used to treat skin infections such as athlete''s foot, jock itch, and ringworm infections. Tolnaftate is also used, along with other antifungals, to treat infections of the nails, scalp, palms, and soles of the feet. The powder and powder aerosol may be used to prevent athlete''s foot.', 'Tolnaftate is a topical fungicide. Though its exact mechanism unknown, it is believed to prevent ergosterol biosynthesis by inhibiting squalene epoxidase. It has also been reported to distort the hyphae and to stunt mycelial growth in susceptible organisms.', 'Tolnaftate is a synthetic over-the-counter anti-fungal agent.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Globexor 150 mg 14 caps.', 19873, 'جلوبيكسور 150مجم 14 كبسولة', '60', NULL, 'Psychiatric.antidepressants', 'Psychiatric.antidepressants', NULL, 'Venlafaxine', 'Global napi pharmaceuticals', 'Cap', 'كبسولة', '150 mg', '2', NULL, NULL, 'about venlafaxine an arylalkanolamine derivative serotonin/norepinephrine reuptakeinhibitor antidepressant anxiolytic. mechanism of action of venlafaxine venlafaxine and its metabolite block the re-uptake of neurotransmitter serotonin and noradrenaline fr', NULL, 959, '2022-07-12', NULL, 0, 'Venlafaxine is indicated for the management of major depressive disorder (MDD), generalized anxiety disorder (GAD), social anxiety disorder (SAD), and panic disorder.[L43030]', 'The exact mechanism of action of venlafaxine in the treatment of various psychiatric conditions has not been fully elucidated; however, it is understood that venlafaxine and its active metabolite O-desmethylvenlafaxine (ODV) potently and selectively inhibits the reuptake of both serotonin and norepinephrine at the presynaptic terminal.[A27600,A252065,L43030] This results in increased levels of neurotransmitters available at the synapse that can stimulate postsynaptic receptors.[A252075] It is suggested that venlafaxine has a 30-fold selectivity for serotonin compared to norepinephrine: venlafaxine initially inhibits serotonin reuptake at low doses, and with higher doses, it inhibits norepinephrine reuptake in addition to serotonin.[A252065,A252070] Venlafaxine and ODV are also weak inhibitors of dopamine reuptake.[L43030]', 'Venlafaxine is an antidepressant agent that works to ameliorate the symptoms of various psychiatric disorders by increasing the level of neurotransmitters in the synapse. Venlafaxine does not mediate muscarinic, histaminergic, or adrenergic effects.[L43030]', 'DrugBank');
