INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Tazorenol 0.1% topical gel', 27951, 'تازورينول0.1% جل موضعي', '13', NULL, 'Psoriasis', 'Psoriasis', NULL, 'Tazarotene', 'Medizen pharmaceutical industries > gast pharma group-egypt', 'Gel', 'جل', '0.1%', '1', NULL, NULL, 'indication used to treat psoriasis acne and sun damaged skin (photodamage). mechanism of action although the exact mechanism of tazarotene action is not known studies have shown that the active form of the drug (tazarotenic acid) binds to all three member', NULL, 1221, '2022-07-07', NULL, 0, 'Used to treat psoriasis, acne and sun damaged skin (photodamage).', 'Although the exact mechanism of tazarotene action is not known, studies have shown that the active form of the drug (tazarotenic acid) binds to all three members of the retinoic acid receptor (RAR) family: RARa, RARb, and RARg, but shows relative selectivity for RARb, and RARg and may modify gene expression. It also has affinity for RXR receptors.', 'Following topical application, tazarotene undergoes esterase hydrolysis to form its active metabolite, tazarotenic acid. When treating acne tazarotene may be taken in conjunction with an oral antibiotic. Tazarotene has been shown in peer-reviewed double blinded studies to reduce: mottling and hyperpigmentation, sallowness, fine wrinkling and coarse wrinkling in sun damaged skin. Histological studies have shown that long term (greater than 1 year) use of Tazarotene is associated with a significant reduction in atypical melanocytes and keratocytes - cells considered to be precursors of skin cancer. Some studies have shown long term use of Tazarotene to be associated with increased collagen production and better organization of skin collagen bundles.', 'DrugBank');
