INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Mucotec 300mg 20 caps.', 8422, 'ميوكوتك 300مجم 20 كبسولة', '144', '76', 'Mucolytic', 'Mucolytic', NULL, 'Erdosteine', 'Global napi pharmaceuticals', 'Cap', 'كبسولة', '300mg', '2', 'مذيب للبلغم', NULL, 'mechanism of action erdosteine an orally mucolytic agent. erdosteine is a thiol derivative developed for the treatment of chronic obstructive bronchitis including acute infective exacerbation of chronic bronchitis. erdosteine contains 2 blocked sulfhydryl', '6223002145973', 7459, '2024-12-19', NULL, 0, 'Fro the treatment of chronic bronchitis in adults.', 'Erdosteine, is an orally administered mucolytic agent. It is classified as a thiol derivative and produced for the management of symptoms caused by chronic obstructive bronchitis.  Erdosteine contains sulfhydryl groups which are released after hepatic first-pass metabolism in the liver. Its active metabolites (3 in number) exert both mucolytic activity and scavenging activity against free radicals. Erdosteine acts to regulate the production of mucus in the airway and regulates its viscosity while enhancing mucociliary transport. This leads to an increase in expectoration. Erdosteine shows inhibition against the effects of free radicals from cigarette smoke. Clinical studies in patients with chronic obstructive lung disease (COPD) have shown that this drug is generally safe and well tolerated. Erdosteine 300mg twice daily reduced cough (both frequency and severity) and sputum viscosity more quickly and more effectively than placebo and reduced the adhesivity of sputum more effectively than ambroxol 30mg twice daily. Co-administration of erdosteine and amoxicillin in patients with acute infective exacerbation of chronic bronchitis resulted in higher concentrations of the antibiotic in the sputum, leading to earlier and more pronounced amelioration of clinical symptoms compared with placebo. Erdosteine is associated with a low incidence of adverse events, most of which are gastrointestinal and generally mild.', NULL, 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Alkasilon 200ml', 13756, 'الكازيلون معلق 200 مل شرب', '3', NULL, 'Antacid', 'Antacid', NULL, 'Aluminium hydroxide+dimethicone+magnesium oxide', 'El nile.', 'Unknown', 'غير محدد', '200ml', '1', NULL, NULL, NULL, '6221077050116', 1000, '2022-12-01', NULL, 0, NULL, 'Aluminum Acetate is an astringent.  An astrignent is a chemical that tends to shrink or constrict body tissues, usually locally after topical medicinal application.  The shrinkage or constriction is through osmotic flow of water (or other fluids) away from the area where the astringent was applied.  Astringent medicines cause shrinkage of mucous membranes or exposed tissues and are often used internally to check discharge of blood serum or mucous secretions. This can happen with a sore throat, hemorrhages, diarrhea, or with peptic ulcers. Externally applied astringents, which cause mild coagulation of skin proteins, dry, harden, and protect the skin. Acne sufferers are often advised to use astringents if they have oily skin. Astringents also help heal stretch marks and other scars. Mild astringent solutions are used in the relief of such minor skin irritations as those resulting from superficial cuts, allergies, insect bites, or fungal infections such as athlete''s foot. ', NULL, 'DrugBank');
