INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31112, 'P-iron 30 mg 20 tab jar', 'بي-ايرون 30مجم 20 قرص برطمان', '110', '98', 'Multivitamin', 'Iron fully reacted amino acid chelated minerals+vit c', 'Copad egypt > plasma pharm', 'Tab', 'أقراص', '30 mg', '1', NULL, 'iron bisglysinat fully reacted amino acid chelated minerals and vit c120 mg', '6225000488816', NULL, 2498, '2025-02-09', 0, 'For treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Also used to replenish body iron stores in Non-Dialysis Dependent-Chronic Kidney Disease (NDD-CKD) patients receiving or not receiving erythropoietin and in Hemodialysis Dependent (HDD-CKD) and Peritoneal Dialysis Dependent (PDD-CKD) - Chronic Kidney Disease patients receiving an erythropoietin.', 'After iron dextran is injected, the circulating iron dextran is removed from the plasma by cells of the reticuloendothelial system, which split the complex into its components of iron and dextran. The iron is immediately bound to the available protein moieties to form hemosiderin or ferritin, the physiological forms of iron, or to a lesser extent to transferrin. This iron which is subject to physiological control replenishes hemoglobin and depleted iron stores.', 'Iron dextran is a dark brown, slightly viscous sterile liquid complex of ferric hydroxide and dextran for intravenous or intramuscular use. It is for treatment of patients with documented iron deficiency in whom oral administration is unsatisfactory or impossible. Iron is essential to the formation of hemoglobin and to the function and formation of other heme and nonheme compounds. Untreated depletion of iron stores leads to iron-deficient erythropoiesis and, in turn, to iron deficiency anemia.', 'DrugBank', 0, 0, 0);
