INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31689, 'Avalon vitamin d 30 capsules', 'افالون فيتامين د 30 كبسولة', '110', '90', 'Multvitamin', 'Vitamin d3', 'Organix company > el-nor pharmaceceutical company', 'Capsule', 'كبسولة', NULL, '3', 'مكمل غذائى يدعم صحة العظام ويمنع الكساح وهشاشة العظام', 'tn = avalon vitamin d 30 capsule gn = vitamin d3 10000 i.u pr = 80 ** supports healthy bones & immunity ** directions: 1 capsule daily', NULL, NULL, 1810, '2024-03-30', 0, 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31691, 'Dyvo facial cleanser', 'ديفو غسول منظف للوجه', '170', NULL, 'Foaming facial cleanser', 'Water (aqua)+glycerin+glycol distearate+licorice extract( glveyrrhiza glabra root extract )+glycolic acid+hyaluronic acid+kojic acid+panthenol+vitamin e+vitamin c (ascorbic acid)+chamomile extract (anthems nobilis flower extract)+honey+alpha- arbutin+rose', 'Smartec > indy cosmetics egypt', 'Cleanser', 'منظف', NULL, '1', NULL, 'gentle non-abrasive exfoliator with glycolic acid to unclog & diminish enlarged pores exfoliate dead skin cells smooth wrinkles & brighten & even out skin tone. combats redness wrinkles aging enlarged pores & blackheads. hydrate brighten & smooth fine lin', NULL, NULL, 605, '2023-03-06', 0, 'For diluting or dissolving drugs for intravenous, intramuscular or subcutaneous injection, according to instructions of the manufacturer of the drug to be administered [FDA Label].', NULL, NULL, 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31693, 'Brovitacan joint 30 f.c.tabs.', 'بروفيتاكان جوينت 30 قرص', '180', '120', 'The ultimate joint care formula', 'Glucosamine sulfate 750 mg+chondroitin sulfate 150 mg+hydrolyzed collagen type ii 100 mg+msm 50 mg+ginkgo biloba 50 mg+vit. c 100 mg+vit. d3 400 iu+manganese 2 mg+copper 1 mg', 'Copad pharmaceutical industries > egycan pharma - elwtania', 'Tab', 'أقراص', NULL, '3', 'مكمل غذائى لعلاج خشونة والتهابات المفاصل واعادة حركتها وليونتها', 'the potent comprehensive formula to support joints and cartilage functions for profelaxis and treatement of all cases of osteoarthrities and osteodegenerative cases', '6225000484511', NULL, 1983, '2024-04-13', 0, 'Glucosamine is generally used over the counter in the symptomatic treatment of osteoarthritis and joint pain,[A2352] frequently combined with chondroitin sulfate and/or ibuprofen.[L32699]', 'The mechanism of action of glucosamine in joint health is unclear,[A231894] however there are several possible mechanisms that contribute to its therapeutic effects. Because glucosamine is a precursor for glycosaminoglycans, and glycosaminoglycans are a major component of joint cartilage,[A232289] glucosamine supplements may help to rebuild cartilage and treat the symptoms of arthritis.  Some in vitro studies show evidence that glucosamine reduces inflammation via inhibition of interferon gamma[A12619,A12616,A12621]  and Nuclear factor kappa B subunit 65 (NF-κB p65),[A12616,A232284] improving the symptoms of arthritis and joint pain. Clinical relevance is unknown at this time.', 'The administration of glucosamine, in theory, provides a building block towards the synthesis of glycosaminoglycans, slowing the progression of osteoarthritis and relieving symptoms of joint pain. Studies to this date examining the efficacy of glucosamine sulfate have been inconclusive. Glycosaminoglycans contribute to joint cartilage elasticity, strength, and flexibility.[A232144] A systematic review of various studies and guidelines determined that modest improvements were reported for joint pain and function in patients taking glucosamine. A consistent joint space narrowing was observed, but with an unclear clinical significance.[A231894]', 'DrugBank', 0, 0, 0);
