INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (31795, 'Deepfreeze pharmakon cold gel 125 gm', 'ديبفريز فارماكون كولد جل 125 جم', '138', NULL, 'Topical analgesic', 'Ethyl chloride+menthol+thymol+eucalyptus oil+camphor+allantoin glycyrrhetinic acid+aloe vera+tocopheryl acetate+isopropyl myristate+melaleuca alternifolia leaf oil', 'Pharmakon', 'Gel', 'جل', '125 gm', '1', NULL, NULL, '6224008056829', NULL, 689, '2023-03-28', 0, 'Investigated for use/treatment in burns and burn infections, cardiac surgery, inflammatory disorders (unspecified), ischemic reperfusion injury, and sepsis and septicemia.', 'CTI-01 inhibits the systemic release of cytokines, such as TNF-alpha and HMGB1, which promote the body''s inflammatory response. The over-expression of these cytokines has been linked to diseases that occur in critical care settings, such as severe organ damage following cardiopulmonary bypass (CPB) and post-operative ileus following abdominal surgery.', NULL, 'DrugBank', 0, 0, 0);
